import React, {Component} from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
import FormData from 'form-data';
import {List, arrayMove} from 'react-movable';

class Media extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            imageUrls: [],
            videoUrls: [],
            type: 'image',
            externalVideoLink: '',
            items: [{name: 'Item 1'}, {name: 'Item 2'}]
        };
        this._handleImageChange = this._handleImageChange.bind(this);
    }

    componentWillReceiveProps(nextProps, nextContext) {
        if (nextProps.media) {
            this.setState({files: nextProps.media})
        }
    }

    componentWillMount() {
        if (this.props.media) {
            this.setState({files: this.props.media})
        }
    }


    _handleImageChange = async (e) => {
        e.preventDefault();
        //reset the chosen photos
        //this.setState({files:[],imageUrls:[]})

        /*
                let files = Array.from(e.target.files);


                //console.log(files)
                files.map((file, index) => {
                    console.log(file, 'inside loop')
                    let reader = new FileReader();
                    reader.readAsDataURL(file);
                    reader.onloadend = () => {
                        let files = [...this.state.files];
                        file.blob = reader.result;
                        files.push(file);
                        this.setState({
                            files: files
                        });

                    }
                });
        */
        let filesArray = Array.from(e.target.files);
        const formData = new FormData();
        filesArray.map((singleFile) => {
            formData.append('files[]', singleFile);
        });
        let response = await fetch('http://localhost:1111/api/uploadMulti', {
            method: 'POST',
            body: formData,
            headers: {
                'Authorization': "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0eXBlIjoyLCJyZWdEYXRlIjoiV2VkIE9jdCAwMiAyMDE5IDEzOjQ1OjA3IEdNVCswMzAwIChBcmFiaWFuIFN0YW5kYXJkIFRpbWUpIiwiaXNWYWxpZCI6dHJ1ZSwiX2lkIjoiNWQ5NDgyMDNlYmQ1ZTg0M2I4NTU5NDI5IiwiZW1haWwiOiJhbGFhIiwibmFtZSI6ImFsYWEiLCJfX3YiOjAsImlhdCI6MTU3MDAxMzc2N30.LB-CBu-kOlPQwJjoyVwhuFP9lYxBWhffh-479W1emX8"
            },
        });
        if (response.ok) {

            try {

                let filesFetched = await response.json();
                let files = [...this.state.files];
                filesFetched.map((singleFile) => {
                    files.push(singleFile);
                });

                this.setState({files});
                this.props.updateMedia(files);
            } catch (e) {
                console.log(e)
            }
        } else {
            console.log('response not ok ', response)
        }


    }


    removeImageFile = (index) => {
        let files = [...this.state.files];
        files.splice(index, 1);
        this.setState({files})
        //call props update
        this.props.updateMedia(files);
    };
    removeVideo = (index) => {
        let videoUrls = [...this.state.videoUrls];
        videoUrls.splice(index, 1);
        this.setState({videoUrls})
    };

    addVideo = () => {
        //check video type and send it to the server
        if (this.state.externalVideoLink !== '') {
            //check if it's a valid link
            let link = this.state.externalVideoLink.toString().toLowerCase();
            if (!link.includes('mp4') || !link.includes('youtube') || !link.includes('www.')) {
                alert('invalid video url (Accepting MP4 or Youtube link');
                return;
            }
            alert('video added');
            this.setState({externalVideoLink: ''})
        } else
            alert('Please fill video link first')
    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    }

    render() {


        return (
            <div>
                <div>
                    <h2>Media</h2>
                </div>

                <Tabs id="controlled-tab-example" activeKey={this.state.type} onSelect={k => this.setState({type: k})}>
                    <Tab eventKey="image" title="Images">
                        <form>
                            <input type="file" onChange={this._handleImageChange} multiple/>
                        </form>

                        <List
                            values={this.state.items}
                            onChange={({oldIndex, newIndex}) =>
                                this.setState(prevState => ({
                                    items: arrayMove(prevState.items, oldIndex, newIndex)
                                }))
                            }
                            renderList={({children, props}) => {
                                return (
                                    <div {...props}>
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <div className={'col'}>
                                                    {children}
                                                </div>
                                            </div>
                                        </ListGroup.Item>
                                    </div>)

                            }}
                            renderItem={({value, props}) => {
                                console.log('the props ', props.key)
                                return (
                                    <div>
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <div className={'col'}>


                                                    <li {...props}>{value.name} </li>
                                                </div>
                                                <Button variant="danger"
                                                        onClick={() => this.removeImageFile(props.key)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>
                                    </div>
                                )
                            }}
                        />

                        {this.state.files.length !== 0 && this.state.files.map((file, index) => {
                            return (
                                <div>
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <div className={'col'}>
                                                {file.type.includes('video') &&
                                                <video width="400" controls>
                                                    <source
                                                        src={file.link}
                                                        type={file.type}
                                                    />
                                                    Your browser does not support HTML5 video.
                                                </video>
                                                }
                                                {file.type.includes('image') &&
                                                <img style={{width: 100, height: 100, padding: 5}} src={file.link}/>}

                                            </div>
                                            <Button variant="danger"
                                                    onClick={() => this.removeImageFile(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                </div>)

                        })}
                    </Tab>
                    <Tab eventKey="video" title="Videos">

                        <Form.Group>
                            <Form.Label>
                                Add video
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Control type="text" placeholder="Video Link (Youtube or external link) MP4"
                                                  onChange={(e) => this.onChange('externalVideoLink', e)}
                                                  value={this.state.externalVideoLink}
                                    />
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addVideo()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                        </Form.Group>

                        {this.state.videoUrls && this.state.videoUrls.map((url, index) => {
                            return (
                                <div>
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <div className={'col'}>

                                                <img style={{width: 100, height: 100, padding: 5}} src={url}/>
                                            </div>
                                            <Button variant="danger"
                                                    onClick={() => this.removeVideo(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                </div>)
                        })}
                    </Tab>

                </Tabs>


            </div>
        )
    }


}

export default Media;
