import React, {Component} from 'react';
import {render} from 'react-dom';
import {SortableContainer, SortableElement} from 'react-sortable-hoc';
import arrayMove from 'array-move';
import {Button, ListGroup} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { SortablePane, Pane } from 'react-sortable-pane';

const SortableItem = SortableElement(({value}) => {
    let file = value;
    console.log('sortable element',file)
    return(
    <div>
        <ListGroup.Item>
            <div>
                <div >
                    {file.type.includes('video') &&
                    <video style={{width: 100, height: 100, padding: 5}} controls>
                        <source
                            src={file.link}
                            type={'video/'+file.link.substr(file.link.lastIndexOf('.')+1,file.link.length)}
                        />
                        Your browser does not support HTML5 video.
                    </video>
                    }
                    {file.type.includes('image') &&
                    <img style={{width: 100, height: 100, padding: 5}} src={file.link}/>}

                </div>
                <Button variant="danger"
                        onClick={() => this.removeImageFile()}>Remove</Button>
            </div>
        </ListGroup.Item>
    </div>)
});

const SortableListC = SortableContainer(({items}) => {
    return (
        <div>
            {items.map((value, index) => (
                <SortableItem key={`item-${value}`} index={index} value={value}/>
            ))}
        </div>
    );
});

export default class SortableList extends Component {
    state = {
        items: [
            {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            }
            , {
                link: "http://localhost:1111/public/imgs/15702971803291555776041y.png",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            } , {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4",
                type: "video",
                _id: "5d98e1abf7d5686fb2dab575"
            }],
    };
    onSortEnd = ({oldIndex, newIndex}) => {
        this.setState(({items}) => ({
            items: arrayMove(items, oldIndex, newIndex),
        }));
    };

    render() {
        return <SortableListC items={this.state.items} onSortEnd={this.onSortEnd}/>;
    }
}
