/**
 * react-drag-sort
 *
 * https://github.com/theuprising/react-drag-sort
 * https://npmjs.com/package/react-drag-sort
 * https://unpkg.com/react-drag-sort/umd/react-drag-sort.min.js
 */

import React from 'react';
import ReactDOM from 'react-dom';
import { sortable } from 'react-sortable';
import {Button,ListGroup} from "react-bootstrap";


class Item extends React.Component {
    render() {
        return (
            <div {...this.props}>
                {this.props.children}
            </div>
        )
    }
}

var SortableItem = sortable(Item);

class SortableList extends React.Component {

    state = {
        items: [
            {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            }
            , {
                link: "http://localhost:1111/public/imgs/15702971803291555776041y.png",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            } , {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4",
                type: "video",
                _id: "5d98e1abf7d5686fb2dab575"
            }
        ]
    };

    onSortItems = (items) => {
        this.setState({
            items: items
        });
    }

    render() {
        const { items } = this.state;
        var listItems = items.map((item, i) => {
            return (
                <SortableItem
                    key={i}
                    onSortItems={this.onSortItems}
                    items={items}
                    sortId={i}>{item}</SortableItem>
            );
        });

        return (
            <ul className='sortable-list'>
                {listItems}
            </ul>
        )
    }
};


export default SortableList
