import React, {Component} from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
import axios from 'axios'
import FormData from 'form-data';

class MediaSingle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
        };
        this._handleImageChange = this._handleImageChange.bind(this);
        this._handleSubmit = this._handleSubmit.bind(this);
    }


    _handleSubmit(e) {
        e.preventDefault();
        // TODO: do something with -> this.state.file

        this.props.updateMedia(this.state.files);

    }

    _handleImageChange = async (e) => {
        e.preventDefault();
        let file = e.target.files;
        if (!file[0].type.toString().includes('image')) {
            alert('allowed type (Images) ONLY');
            return;
        }
        //reset the chosen photos
        this.setState({files: []});


        const formData = new FormData();
        formData.append('file', file[0])
        let response = await fetch('http://localhost:1111/api/upload', {
            method: 'POST',
            body: formData,
            headers: {
                'Authorization': "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0eXBlIjoyLCJyZWdEYXRlIjoiV2VkIE9jdCAwMiAyMDE5IDEzOjQ1OjA3IEdNVCswMzAwIChBcmFiaWFuIFN0YW5kYXJkIFRpbWUpIiwiaXNWYWxpZCI6dHJ1ZSwiX2lkIjoiNWQ5NDgyMDNlYmQ1ZTg0M2I4NTU5NDI5IiwiZW1haWwiOiJhbGFhIiwibmFtZSI6ImFsYWEiLCJfX3YiOjAsImlhdCI6MTU3MDAxMzc2N30.LB-CBu-kOlPQwJjoyVwhuFP9lYxBWhffh-479W1emX8"
            },
        })
        console.log('response is', response);


        let reader = new FileReader();

        reader.readAsDataURL(file[0]);
        reader.onloadend = () => {
            let files = [...this.state.files];

            file[0].blob = reader.result;
            files.push(file[0]);
            this.setState({
                files: files
            });

        }


    }

    removeImageFile = (index) => {
        this.setState({files: []})
    };


    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    };

    render() {


        return (
            <div>

                <form onSubmit={this._handleSubmit}>
                    <div className={"row p-2"}>
                        <input type="file" onChange={this._handleImageChange} className={"col"}/>
                        <button type="submit" onClick={this._handleSubmit}>Upload Image</button>
                    </div>
                </form>
                {this.state.files && this.state.files.map((fileUrl, index) => {
                    return (
                        <div>
                            <ListGroup.Item>
                                <div className={'row'}>
                                    <div className={'col'}>
                                        {file.type.includes('image') &&
                                        <img style={{width: 100, height: 100, padding: 5}} src={fileUrl}/>}

                                    </div>
                                    <Button variant="danger"
                                            onClick={() => this.removeImageFile(index)}>Remove</Button>
                                </div>
                            </ListGroup.Item>
                        </div>)

                })}


            </div>
        )
    }


}

export default MediaSingle;
