import React, {Component} from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'

class SocialMedia extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            imageUrls: [],
            videoUrls: [],
            type: 'image',
            socialMediaList: this.props.socialMediaList,
            platforms: ['facebook', 'instagram', 'linkedin', 'twitter', 'google', 'youtube', 'angellist', 'codepen', 'envelope', 'etsy', 'flickr', 'foursquare', 'github-alt', 'github', 'gitlab', 'medium', 'pinterest', 'quora', 'reddit-alien', 'soundcloud', 'stack-overflow', 'steam', 'stumbleupon', 'tumblr', 'twitch', 'google-plus-official', 'vimeo', 'vk', 'weibo', 'wordpress', 'other'],
            selectedPlatform: '',
            socialMediaLink:'',
            icon:'',

        };
        this._handleImageChange = this._handleImageChange.bind(this);
        this._handleSubmit = this._handleSubmit.bind(this);
    }

    _handleSubmit(e) {
        e.preventDefault();
        // TODO: do something with -> this.state.file
    }


    _handleImageChange(e) {
        e.preventDefault();
        //reset the chosen photos
        //this.setState({files:[],imageUrls:[]})


        let files = Array.from(e.target.files);


        console.log(files);

    }

    removeImageFile = (index) => {
        let files = [...this.state.files];
        let imageUrls = [...this.state.imageUrls];
        files.splice(index, 1);
        imageUrls.splice(index, 1);
        this.setState({files, imageUrls})
    };
    removeSocialMedia = (index) => {
        let socialMediaList = [...this.state.socialMediaList];
        socialMediaList.splice(index, 1);
        this.setState({socialMediaList},() =>this.props.updateSocialMediaList(this.state.socialMediaList))

    };
    videoType = (e) => {
        console.log('video select change', e.target.value);
    }
    addSocialMedia = () => {
        //check if link is empty
        if(this.state.socialMediaLink === ''){
            alert('Please fill social media link first')
            return;
        }
        let socialMediaList = [...this.state.socialMediaList];
        console.log('the linkn is ',this.state.socialMediaLink)
        //create the obbject
        let socialMedia = {
            platform:this.state.selectedPlatform,
            link:this.state.socialMediaLink,
            icon:this.state.icon,
        }
        socialMediaList.push(socialMedia);
        console.log('social list before updating',socialMediaList);
        this.setState({socialMediaList,socialMediaLink:'',icon:''});
        this.props.updateSocialMediaList(socialMediaList)
    };

    componentWillMount() {
        this.setState({selectedPlatform: this.state.platforms[0]})
    }

    onChangePlatformSelection = (e) => {
        this.setState({selectedPlatform: e.target.value})
    }

    componentWillReceiveProps(nextProps, nextContext) {
        console.log('will rcv props', nextProps)
        this.setState({socialMediaList: nextProps.socialMediaList})
    }
    onChangeText = (key,e) =>{
      this.setState({[key]:e.target.value})
    };

    render() {


        return (
            <div>
                <div>
                    <h2>Social Media</h2>
                </div>

                <Form.Group>
                    <Form.Label>
                        Add Social Media
                    </Form.Label>
                    <Form.Row>
                        <Form.Group as={Col}>
                            <FormControl as={'select'} onChange={this.onChangePlatformSelection}>
                                {this.state.platforms.map(platform => {
                                    return (
                                        <option>{platform}</option>
                                    )
                                })}
                            </FormControl>
                        </Form.Group>

                        {this.state.selectedPlatform === 'other' && <Form.Group as={Col}>
                            <Form.Control type="text" placeholder="Platform icon link"/>
                        </Form.Group>}



                        <Form.Group as={Col}>
                            <Form.Control type="text" placeholder="Social media link" onChange={(e)=>this.onChangeText('socialMediaLink',e)} value={this.state.socialMediaLink}/>
                        </Form.Group>

                        <Form.Group as={Col} className={'center'}>
                            <Button className="badge-info col-6" onClick={() => this.addSocialMedia()}><i
                                className="fa fa-plus"/> Add</Button>
                        </Form.Group>
                    </Form.Row>
                    {this.state.socialMediaList.length !== 0 ? this.state.socialMediaList.map((socialMedia, index) => {
                            let className = 'fa fa-' + socialMedia.platform;
                            return (
                                <ListGroup.Item>
                                    <div className={'row'}>
                                        <i className={className} style={{width: 64}}/>
                                        <FontAwesomeIcon icon={'facebook'} style={{width: 64}}/>
                                        <p className={'col'}>{socialMedia.link}</p>
                                        <Button variant="danger"
                                                onClick={() => this.removeSocialMedia(index)}>Remove</Button>
                                    </div>
                                </ListGroup.Item>
                            )
                        }) :
                        <ListGroup.Item>
                            <p className={'col'}>No Social media yet</p>
                        </ListGroup.Item>
                    }

                </Form.Group>



            </div>
        )
    }


}

export default SocialMedia;
