import React, {Component} from 'react';
import {Button, Col, Container, Form, Modal, Row} from "react-bootstrap";
import MediaSingle from "./MediaSingle";

class AddSubCategory extends Component {


    handleClose = () => {
        this.setState({modal: false});
        if(this.props.handleClose)
            this.props.handleClose();
    }

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        if (this.state.modal !== nextProps.modal)
            this.setState({modal: nextProps.modal});
        if(nextProps.edit){
            let subCategory =nextProps.category.subCategories[nextProps.subCategoryIndex];
            if(subCategory){
                this.setState({
                    edit:true,
                    title:'Edit',
                    name:subCategory.name,
                    nameAr:subCategory.nameAr,
                    icon:subCategory.icon,

                })
            }

        }
    }

    state = {
        name: '',
        nameAr: '',
        icon: '',
        modal: false,
        edit:false,
        title:'Add',
    };


    addMediaFile = (icon) => {
        this.setState({icon});
        //console.log(icon)
    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    }

    addSubCategory = () => {
        if (this.state.name === '' || this.state.nameAr === '' || this.state.icon === '') {
            alert('please fill all fields');
            return;
        }
        this.props.addSubCategory({
            name: this.state.name,
            nameAr: this.state.nameAr,
            icon: this.state.icon
        });
        this.setState({name: '', nameAr: '', icon: ''})
        this.handleClose();
    };

    render() {
        return (
            <Container class>

                <Modal show={this.state.modal} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>{this.state.title} SubCategory</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>
                        <Form>
                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Name (English)
                                </Form.Label>
                                <Col>
                                    <Form.Control type="text" placeholder="Category name" value={this.state.name}
                                                  onChange={(e) => this.onChange('name', e)}/>
                                </Col>

                            </Form.Group>
                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Name (Arabic)
                                </Form.Label>
                                <Col>
                                    <Form.Control type="text" placeholder="Category name (Arabic)"
                                                  value={this.state.nameAr}
                                                  onChange={(e) => this.onChange('nameAr', e)}/>
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    SubCategory Icon
                                </Form.Label>

                                <MediaSingle updateMedia={this.addMediaFile} files={this.state.icon}/>


                            </Form.Group>

                        </Form>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Cancel
                        </Button>
                        <Button variant="primary" onClick={() => {
                            this.addSubCategory()
                        }}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>


            </Container>

        );
    }
}

export default AddSubCategory;
