import React, {Component} from 'react';
import {Button, Col, Container, Form, FormControl, ListGroup, Modal, Row} from "react-bootstrap";
import Media from "./Media";
import MediaSingle from "./MediaSingle";

class AddSubCategory extends Component {


    handleClose = () => this.setState({modal: false});
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        //if (this.state.modal !== nextProps.modal)
            this.setState({modal: nextProps.modal})
    }

    state = {
        name: '',
        nameAr: '',
        iconFile: [],
        modal: false,
    };


    addMediaFile(iconFile) {
        //let iconFile = Array.from(files);
        this.setState({iconFile})
        console.log(iconFile)
        //const data = new FormData();
        //data.append('file', iconFile);

    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    }

    render() {
        return (
            <Container class>

                <Modal show={this.state.modal} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add SubCategory</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>
                        <Form>
                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Name (English)
                                </Form.Label>
                                <Col>
                                    <Form.Control type="text" placeholder="Category name" value={this.state.name}
                                                  onChange={(e) => this.onChange('name', e)}/>
                                </Col>

                            </Form.Group>
                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Name (Arabic)
                                </Form.Label>
                                <Col>
                                    <Form.Control type="text" placeholder="Category name (Arabic)"
                                                  value={this.state.nameAr}
                                                  onChange={(e) => this.onChange('nameAr', e)}/>
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    SubCategory Icon
                                </Form.Label>

                                <MediaSingle updateMedia={this.addMediaFile}/>


                            </Form.Group>

                        </Form>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Cancel
                        </Button>
                        <Button variant="primary" onClick={()=>{
                            this.props.addSubCategory({
                                name: this.state.name,
                                nameAr: this.state.nameAr,
                                iconFile: this.state.iconFile
                            });
                            this.setState({name:'',nameAr:'',iconFile:[]})
                        }}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>


            </Container>

        );
    }
}

export default AddSubCategory;
