import React, {Component} from 'react';
import {Table} from "react-bootstrap";
import baseService from "../services/baseService";
import ConfirmationModal from "./ConfirmationModal";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";


class Appointments extends Component {

    state = {
        appointments: [],
        appointmentsFiltered: [],
        show: false,
        selectedAppointment: {},
        tab: 'All'
    };

    componentWillMount() {
        baseService.getAppointments().then((response) => {
            this.setState({appointments: response, appointmentsFiltered: response})
        }).catch(err => console.log(err))
    }

    eventClicked = (e) => {
        alert(e);
    }

    close = () => {
        this.setState({show: false})
    };
    updateAppointmentStatus = async (status) => {
        let newAppointment = this.state.selectedAppointment;
        newAppointment.status = status;
        await baseService.updateAppointment(newAppointment);
    }
    accept = () => {
        this.updateAppointmentStatus('Accepted')
        this.close();
    }
    reject = () => {
        this.updateAppointmentStatus('Rejected')
        this.close();
    }
    timeConvert = (time) => {
        //expecting array of 2 times
        let newTime = time.split(' to ');
        newTime.map((singleTime, index) => {
            // Check correct time format and split into components
            singleTime = singleTime.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

            if (singleTime.length > 1) { // If time format correct
                singleTime = singleTime.slice(1);  // Remove full string match value
                singleTime[5] = +singleTime[0] < 12 ? 'AM' : 'PM'; // Set AM/PM
                singleTime[0] = +singleTime[0] % 12 || 12; // Adjust hours
            }

            newTime[index] = singleTime.join('');
            if (index === 0)
                newTime[index] += " to ";


        });
        //apply (to) string


        return newTime; // return adjusted time or original string
    };
    filter = (filterCondition) => {
        let appoinntments = [...this.state.appointments];
        appoinntments = appoinntments.filter((singleAppointment) => {
            if (filterCondition === "Pending")
                return singleAppointment.status == filterCondition;
            else if (filterCondition === "Coming")
                return true;
            else if (filterCondition === "Passed")
                return true
        })
        this.setState({appointmentsFiltered: appoinntments})

    };
    onAppointmentClick = (e,selectedAppointment) => {
        e.preventDefault();
        this.setState({selectedAppointment, show: true})

    }
    renderAppointments = () => {
        return (
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Username</th>
                    <th>Request Date</th>
                    <th>Appointment Time</th>
                    <th>Appointment Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {this.state.appointmentsFiltered.length > 0 ? this.state.appointmentsFiltered.map((singleAppointment, index) => {
                    return (

                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{singleAppointment.clientId.username}</td>
                            <td>{singleAppointment.requestDate}</td>
                            <td>{this.timeConvert(singleAppointment.time)}</td>
                            <td>{singleAppointment.appointmentDate}</td>
                            <td>{singleAppointment.status}</td>
                            <td>{singleAppointment.status !=="Pending" ?
                                <p>Action Taken</p>:
                                <a href='' onClick={(e) => this.onAppointmentClick(e,singleAppointment)}>Take Action</a>}
                            </td>
                        </tr>
                    )
                }) : <tr>
                    <td colSpan={7}>
                        <center>there is no appointments yet</center>
                    </td>
                </tr>}
                </tbody>
            </Table>
        )
    }

    render() {
        return (
            <div>
                <h2>Appointments</h2>

                <Tabs id="controlled-tab-example" activeKey={this.state.tab} onSelect={k => this.setState({tab: k})}>
                    <Tab eventKey="All" title="All Appointments" mountOnEnter={true} onClick={()=>alert('clicked')}
                         onEnter={() => this.filter('Pending')}>

                    </Tab>
                    <Tab eventKey="Pending" title="Pending Appointments" onEnter={() => this.filter('Pending')}>

                    </Tab>
                    <Tab eventKey="Coming" title="Coming Appointments" onEnter={() => this.filter('Coming')}>
                    </Tab>
                    <Tab eventKey="Past" title="Past Appointments" onEnter={() => this.filter('Past')}>
                    </Tab>
                </Tabs>
                {this.renderAppointments()}

                <ConfirmationModal show={this.state.show} close={this.close} accept={this.accept} reject={this.reject}/>
            </div>
        );
    }
}

export default Appointments;
