import React, {Component} from 'react';
import {Button, Col, Container, Form, FormControl, ListGroup, Modal, Row} from "react-bootstrap";
import Media from "./Media";
import MediaSingle from "./MediaSingle";
import AddSubCategory from "./AddSubCategory";

class AddCategory extends Component {

    state = {
        subCategories: [],
        category: {},
        name: '',
        nameAr: '',
        icon: '',
        modal: false,
        subCategoryModal: false,
        edit: false,
        title: 'Add',

    };

    handleClose = () => {
        this.setState({modal: false, subCategoryModal: false});

        this.props.handleClose();
    }

    addSubCategory = (category) => {
        let subCategories = [...this.state.subCategories];
        subCategories.push(category);//consider validations
        this.setState({subCategories, subCategoryModal: false});
    }

    addMediaFile = (icon) => {
        this.setState({icon});
        //console.log(icon)
    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    };

    addCategory = () => {
        if (this.state.name === '' || this.state.nameAr === '' || this.state.icon === '' || this.state.subCategories.length === 0) {
            alert('please fill all fields');
            return;
        }
        if (this.state.edit) {
            //edit method
            let category = this.state.category;
            category.name = this.state.name;
            category.nameAr = this.state.nameAr;
            category.icon = this.state.icon;
            category.subCategories = this.state.subCategories;
            this.props.editCategory(category).catch((err) => console.log(err))
        } else {
            this.props.addCategory({
                name: this.state.name,
                nameAr: this.state.nameAr,
                icon: this.state.icon,
                subCategories: this.state.subCategories
            });
        }
        this.setState({name: '', nameAr: '', icon: '', subCategories: [], edit: false, title: 'Add'})
        this.handleClose();
    };

    componentWillReceiveProps(nextProps, nextContext) {
        if (this.state.modal !== nextProps.modal)
            this.setState({modal: nextProps.modal});
        if (nextProps.edit) {
            let category = nextProps.category;
            if (category) {
                //edit category
                this.setState({
                    category: nextProps.category,
                    title: 'Edit',
                    edit: true,
                    name: category.name,
                    nameAr: category.nameAr,
                    icon: category.icon,
                    subCategories: category.subCategories,

                })
            }

        }

    }

    removeSubCategory = (subCategoryIndex) => {
        let subCategories = [...this.state.subCategories];
        subCategories.splice(subCategoryIndex, 1);
        this.setState({subCategories})
    };

    render() {
        return (
            <Container class>
                <Modal show={this.state.modal} onHide={this.handleClose} size="lg">
                    <Modal.Header closeButton>
                        <Modal.Title>{this.state.title} Category</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>

                        <Form>
                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Name (English)
                                </Form.Label>
                                <Col sm="4">
                                    <Form.Control type="text" placeholder="Category name"
                                                  value={this.state.name}
                                                  onChange={(e) => this.onChange('name', e)}/>
                                </Col>
                                <Form.Label column sm="2">
                                    Name (Arabic)
                                </Form.Label>
                                <Col sm="4">
                                    <Form.Control type="text" placeholder="Category name (Arabic)"
                                                  value={this.state.nameAr}
                                                  onChange={(e) => this.onChange('nameAr', e)}/>
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row}>
                                <Form.Label column sm="2">
                                    Category Icon
                                </Form.Label>
                                <Col>
                                    <MediaSingle updateMedia={this.addMediaFile} icon={this.state.icon}/>
                                </Col>

                            </Form.Group>


                            <Form.Group as={Row} className={'col'}>
                                <Button className="badge-info col"
                                        onClick={() => this.setState({subCategoryModal: true})}><i
                                    className="fa fa-plus"/> Add SubCategory</Button>
                            </Form.Group>

                            <ListGroup>


                                {this.state.subCategories.length !== 0 ? this.state.subCategories.map((category, index) => {
                                        return (
                                            <ListGroup.Item>
                                                <div className={'row'}>
                                                    <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                    <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                    <Button variant="danger"
                                                            onClick={() => this.removeSubCategory(index)}>Remove</Button>
                                                </div>
                                            </ListGroup.Item>
                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No Subcategories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>

                            <AddSubCategory modal={this.state.subCategoryModal}
                                            handleClose={() => this.setState({subCategoryModal: false})}
                                            addSubCategory={this.addSubCategory}/>

                        </Form>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Cancel
                        </Button>
                        <Button variant="primary" onClick={() => {
                            this.addCategory()
                        }}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>

            </Container>

        );
    }
}

export default AddCategory;
