import React, {Component} from 'react';
import {Button, Col, Form, ListGroup, Row} from "react-bootstrap";
import baseService from '../services/baseService'
import Table from "react-bootstrap/Table";
import AddSubCategory from "./AddSubCategory";
import AddCategory from "./AddCategory";

class Categories extends Component {
    state = {
        categories: [],
        loading: true,
        selectedIndex: 0,
        categoriesModal:false,
        subCategoriesModal:false
    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({
            categories,
            loading: false
        })).catch((err) => console.log(err));
    }

    onCategoryClick = (index) => {
        this.setState({selectedIndex: index});
    };
    removeCategory = (index) => {
        let categories = [...this.state.categories];
        categories.splice(index, 1);
        this.setState({categories});
    };
    removeSubCategory = (categoryIndex,subCategoryIndex) => {
        let categories = [...this.state.categories];
        let category = categories[categoryIndex];
        category.subCategories.splice(subCategoryIndex, 1);
        categories[categoryIndex] = category;
        this.setState({categories})
    };
    addCategory = () => {
        //add category
    };
    addSubCategory = () => {
        //add sub category
    };

    render() {
        return (
            <div>
                <h2>Categories</h2>

                <Form>

                    <Form.Row>
                        <Form.Group>
                            <ListGroup>

                                <ListGroup.Item>
                                    <Button className="badge-info col" onClick={() => this.setState({categoryModal:true})}><i
                                        className="fa fa-plus"/> Add Category</Button>
                                </ListGroup.Item>
                                {this.state.categories.length !== 0 ? this.state.categories.map((category, index) => {
                                        let active = this.state.selectedIndex === index;
                                        return (
                                            this.state.selectedIndex === index ?
                                                <ListGroup.Item onClick={() => this.onCategoryClick(index)} active>
                                                    <div className={'row'}>
                                                        <img style={{width: 64, height: 64, padding: 5}}
                                                             src={category.icon}/>
                                                        <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                                           onClick={() => this.removeCategory(index)}/>
                                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>

                                                    </div>
                                                </ListGroup.Item>
                                                : <ListGroup.Item onClick={() => this.onCategoryClick(index)}>
                                                    <div className={'row'}>
                                                        <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                        <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                                           onClick={() => this.removeCategory(index)}/>
                                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>

                                                    </div>
                                                </ListGroup.Item>

                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No categories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>

                        </Form.Group>

                        <Form.Group>
                            <ListGroup>
                                <ListGroup.Item>
                                    <Button className="badge-info col" onClick={() => this.setState({subCategoriesModal:true})}>
                                        <i className="fa fa-plus"/> Add SubCategory</Button>
                                </ListGroup.Item>
                                {this.state.categories.length > 0 && this.state.categories[this.state.selectedIndex].subCategories.length > 0 ? this.state.categories[this.state.selectedIndex].subCategories.map((category, index) => {
                                        return (
                                            <ListGroup.Item>
                                                <div className={'row'}>
                                                    <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                    <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                    <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                                       onClick={() => this.removeSubCategory(index)}/>
                                                    <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                                </div>
                                            </ListGroup.Item>
                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No Sub Categories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>
                        </Form.Group>
                    </Form.Row>
                </Form>


                <Form>
                    <Button variant="success" onClick={() => this.setState({categoriesModal:true})}>
                        <i className="fa fa-plus"/> Add Category
                    </Button>

                    <Table striped bordered hover>
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>Icon</th>
                            <th>Category Name</th>
                            <th>Category Name (Ar)</th>
                            <th>SubCategories</th>
                            <th>Actions</th>
                        </tr>
                        </thead>
                        <tbody>

                        {this.state.categories.length > 0 ? this.state.categories.map((category, categoryIndex) => {
                            return (
                                <tr>
                                    <td>{categoryIndex}</td>
                                    <td>
                                        <center>
                                            <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                        </center>
                                    </td>
                                    <td>{category.name}</td>
                                    <td>{category.nameAr}</td>
                                    <td>
                                        <Button variant="success">
                                            <i className="fa fa-plus"/> Add SubCategory
                                        </Button>

                                        <Table striped bordered hover>
                                            <thead>
                                            <tr>
                                                <th>#</th>
                                                <th>Icon</th>
                                                <th>Category Name</th>
                                                <th>Category Name (Ar)</th>
                                                <th>Actions</th>
                                            </tr>
                                            </thead>
                                            <tbody>
                                            {category.subCategories.length > 0 ? category.subCategories.map((subCategory, subCategoryIndex) => {
                                                //do whatever
                                                return (
                                                    <tr>
                                                        <td>{subCategoryIndex}</td>
                                                        <td>
                                                            <center>
                                                                <img style={{width: 64, height: 64, padding: 5}}
                                                                     src={subCategory.icon}/>
                                                            </center>
                                                        </td>
                                                        <td>{subCategory.name}</td>
                                                        <td>{subCategory.nameAr}</td>
                                                        <td>
                                                            <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={() => this.removeSubCategory(categoryIndex,subCategoryIndex)}/>
                                                            <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                                        </td>
                                                    </tr>
                                                )
                                            }):  <tr > <td colSpan="5"><center>No subcategories yet</center></td></tr>}

                                            </tbody>
                                        </Table>
                                    </td>
                                    <td>
                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={() => this.removeCategory(categoryIndex)}/>
                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                    </td>
                                </tr>
                            )
                        }): <tr > <td colSpan="6"><center>No categories yet</center></td></tr>}

                        </tbody>
                    </Table>
                </Form>

                <AddCategory modal={this.state.modal} addCategory={this.addCategory}/>
            </div>
        );
    }
}

export default Categories;
