import React, { Component } from 'react';
import {Button, Form, ListGroup} from "react-bootstrap";
import baseService from '../services/baseService'

class Categories extends Component {
    state ={
        categories:[],
        loading:true
    };
    componentWillMount() {
        baseService.getCategories().then((categories)=>this.setState({categories,loading:false})).catch(err=>console.log(err));
    }

    render() {
        return (
            <div>
                <h2>Categories</h2>

                <ListGroup>


                    {this.state.categories.length !== 0 ? this.state.categories.map((category, index) => {
                            return (
                                <ListGroup.Item>
                                    <div className={'row'}>
                                        <p className={'col'}>{category}</p>
                                        <Button variant="danger"
                                                onClick={() => this.removeCategory(index)}>Remove</Button>
                                    </div>
                                </ListGroup.Item>
                            )
                        }) :
                        <ListGroup.Item>
                            <p className={'col'}>No categories yet</p>
                        </ListGroup.Item>
                    }


                </ListGroup>

            </div>
        );
    }
}

export default <Categories></Categories>;
