import React, {Component} from 'react';
import {Button, Col, Form, ListGroup, Row} from "react-bootstrap";
import baseService from '../services/baseService'
import Table from "react-bootstrap/Table";
import AddSubCategory from "./AddSubCategory";
import AddCategory from "./AddCategory";

class Categories extends Component {
    state = {
        categories: [],
        loading: true,
        categoriesModal: false,
        subCategoriesModal: false,
        categoryIndex: -1, //for adding subcategory / edit category
        subCategoryIndex:-1, //for editing the sub category
        edit:false,

    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({
            categories,
            loading: false
        })).catch((err) => console.log(err));
    }

    removeCategory = (index) => {
        let categories = [...this.state.categories];
        categories.splice(index, 1);
        this.setState({categories});
    };
    removeSubCategory = (categoryIndex, subCategoryIndex) => {
        let categories = [...this.state.categories];
        let category = categories[categoryIndex];
        category.subCategories.splice(subCategoryIndex, 1);
        categories[categoryIndex] = category;
        this.setState({categories})
    };
    addCategory = (category) => {
        //add category
        let categories = [...this.state.categories];
        categories.push(category);
        this.setState({categories, categoriesModal: false});

    };
    addSubCategory = (subCategory) => {
        //add sub category
        let categories = [...this.state.categories];
        if (this.state.categoryIndex !== -1) {
            let category = categories[this.state.categoryIndex];
            category.subCategories.push(subCategory);
            this.setState({categories, categoryIndex: -1});
        } else {
            alert('Failed to add sub category');
        }

    };

    addSubCategoryBtn = (categoryIndex) => {
        //set category inedx state
        this.setState({categoryIndex: categoryIndex, subCategoriesModal: true});
    };

    editCategory = (categoryIndex,subCategoryIndex) =>{

    };

    editSubCategory = (categoryIndex) =>{

    }

    render() {
        return (
            <div>
                <h2>Categories</h2>


                <Form>
                    <Button variant="success" onClick={() => this.setState({categoriesModal: true})}>
                        <i className="fa fa-plus"/> Add Category
                    </Button>

                    <Table striped bordered hover>
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>Icon</th>
                            <th>Category Name</th>
                            <th>Category Name (Ar)</th>
                            <th>SubCategories</th>
                            <th>Actions</th>
                        </tr>
                        </thead>
                        <tbody>

                        {this.state.categories.length > 0 ? this.state.categories.map((category, categoryIndex) => {
                            return (
                                <tr>
                                    <td>{categoryIndex}</td>
                                    <td>
                                        <center>
                                            <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                        </center>
                                    </td>
                                    <td>{category.name}</td>
                                    <td>{category.nameAr}</td>
                                    <td>
                                        <Button variant="success" onClick={() => this.addSubCategoryBtn(categoryIndex)}>
                                            <i className="fa fa-plus"/> Add SubCategory
                                        </Button>

                                        <Table striped bordered hover>
                                            <thead>
                                            <tr>
                                                <th>#</th>
                                                <th>Icon</th>
                                                <th>Category Name</th>
                                                <th>Category Name (Ar)</th>
                                                <th>Actions</th>
                                            </tr>
                                            </thead>
                                            <tbody>
                                            {category.subCategories.length > 0 ? category.subCategories.map((subCategory, subCategoryIndex) => {
                                                //do whatever
                                                return (
                                                    <tr>
                                                        <td>{subCategoryIndex}</td>
                                                        <td>
                                                            <center>
                                                                <img style={{width: 64, height: 64, padding: 5}}
                                                                     src={subCategory.icon}/>
                                                            </center>
                                                        </td>
                                                        <td>{subCategory.name}</td>
                                                        <td>{subCategory.nameAr}</td>
                                                        <td>
                                                            <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                                               onClick={() => this.removeSubCategory(categoryIndex, subCategoryIndex)}/>
                                                            <i className="fa fa-edit fa-2x" style={{color: 'green'}}
                                                               onClick={() => this.setState({
                                                                   categoryIndex: categoryIndex,
                                                                   subCategoryIndex:subCategoryIndex,
                                                                   edit: true,
                                                                   subCategoryModal:true
                                                               })}/>
                                                        </td>
                                                    </tr>
                                                )
                                            }) : <tr>
                                                <td colSpan="5">
                                                    <center>No subcategories yet</center>
                                                </td>
                                            </tr>}

                                            </tbody>
                                        </Table>
                                    </td>
                                    <td>
                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                           onClick={() => this.removeCategory(categoryIndex)}/>
                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}
                                           onClick={() => this.setState({
                                               categoryIndex:categoryIndex,
                                               edit:true,
                                               categoryModal:true
                                           })}/>
                                    </td>
                                </tr>
                            )
                        }) : <tr>
                            <td colSpan="6">
                                <center>No categories yet</center>
                            </td>
                        </tr>}

                        </tbody>
                    </Table>
                </Form>

                <AddCategory modal={this.state.categoriesModal}
                             addCategory={this.addCategory}
                             edit={this.state.edit}
                             editCategory={this.editCategory}
                             category={this.state.categories[this.state.categoryIndex]}
                />
                <AddSubCategory modal={this.state.subCategoriesModal}
                                handleClose={() => this.setState({subCategoriesModal: false, categoryIndex: -1})}
                                addSubCategory={this.addSubCategory}
                                edit={this.state.edit}
                                editSubCategory={this.editSubCategory}
                                subCategory={this.state.categories[this.state.categoryIndex].subCategories[this.state.subCategoryIndex]}
                />
            </div>
        );
    }
}

export default Categories;
