/**
 * react-drag-sort
 *
 * https://github.com/theuprising/react-drag-sort
 * https://npmjs.com/package/react-drag-sort
 * https://unpkg.com/react-drag-sort/umd/react-drag-sort.min.js
 */

import React from 'react';
import ReactDOM from 'react-dom';
import { sortable } from 'react-sortable';
import {Button,ListGroup} from "react-bootstrap";


class Item extends React.Component {
    render() {
        return (
            <div {...this.props}>
                {this.props.children}
            </div>
        )
    }
}

var SortableItem = sortable(Item);

class SortableList extends React.Component {

    state = {
        items: [
            {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            }
            , {
                link: "http://localhost:1111/public/imgs/15702971803291555776041y.png",
                type: "image",
                _id: "5d98e1abf7d5686fb2dab575"
            } , {
                link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4",
                type: "video",
                _id: "5d98e1abf7d5686fb2dab575"
            }
        ]
    };

    onSortItems = (items) => {
        this.setState({
            items: items
        });
    }

    render() {
        const { items } = this.state;
        var listItems = items.map((item, i) => {
            return (
                <SortableItem
                    key={i}
                    onSortItems={this.onSortItems}
                    items={items}
                    sortId={i}>{item}</SortableItem>
            );
        });

        return (
            <div className='sortable-list'>
                {listItems.map((file,index) =>{
                    console.log('file sis ',file);
                    return(
                        <div>
                            <ListGroup.Item>
                                <div >
                                    <div >
                                        { (file.type.includes('video') && !file.link.toString().toLowerCase().includes('youtube')) ?
                                            <video style={{width: 200, height: 250, padding: 5}} controls>
                                                <source
                                                    src={file.link}
                                                    type={'video/' + file.link.substr(file.link.lastIndexOf('.') + 1, file.link.length)}
                                                />
                                                Your browser does not support HTML5 video.
                                            </video> : file.type.includes('video') && file.link.toString().toLowerCase().includes('youtube') &&
                                            <iframe width="200" height="250"
                                                    src={file.link.replace('watch?v=','embed/').split('&')[0]} frameBorder="0"
                                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                    allowFullScreen/>
                                        }
                                        {file.type.includes('image') &&
                                        <img style={{width: 100, height: 100, padding: 5}} src={file.link}/>}

                                    </div>
                                    <Button variant="danger"
                                            onClick={() => this.removeImageFile(index)}>Remove</Button>
                                </div>
                            </ListGroup.Item>
                        </div>
                    )
                })}
            </div>
        )
    }
};


export default SortableList
