import React, {Component} from 'react';
import {Button, Col, Container, Form, FormControl, ListGroup, Modal, Row} from "react-bootstrap";
import Media from "./Media";
import MediaSingle from "./MediaSingle";
import AddSubCategory from "./AddSubCategory";

class AddCategory extends Component {

    state = {
        subCategories: [],
        name: '',
        nameAr: '',
        icon: '',
        modal:false,
    };

    render() {
        return (
            <Container class>
                <h2>Add Category</h2>


                <Form>
                    <Form.Group as={Row}>
                        <Form.Label column sm="2">
                            Name (English)
                        </Form.Label>
                        <Col sm="4">
                            <Form.Control type="text" placeholder="Category name"/>
                        </Col>
                        <Form.Label column sm="2">
                            Name (Arabic)
                        </Form.Label>
                        <Col sm="4">
                            <Form.Control type="text" placeholder="Category name (Arabic)"/>
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                        <Form.Label column sm="2">
                           Category Icon
                        </Form.Label>
                        <Col >
                            <MediaSingle />
                        </Col>

                    </Form.Group>


                    <Form.Group as={Row} className={'col'}>
                        <Button className="badge-info col" onClick={() => this.addCategory()}><i
                            className="fa fa-plus"/> Add SubCategory</Button>
                    </Form.Group>

                    <ListGroup>


                        {this.state.subCategories.length !== 0 ? this.state.subCategories.map((category, index) => {
                                return (
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <p className={'col'}>{category.name} / {category.nameAr}</p>
                                            <Button variant="danger"
                                                    onClick={() => this.removeSubCategory(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                )
                            }) :
                            <ListGroup.Item>
                                <p className={'col'}>No Subcategories yet</p>
                            </ListGroup.Item>
                        }


                    </ListGroup>

<SubCategoryModal modal={this.state.modal}/>
                    <Button variant="primary" onClick={() => {
                        this.setState({modal: true})
                    }}>Submit</Button>
                </Form>


            </Container>

        );
    }
}

export default AddCategory;

class SubCategoryModal extends Component {
    state = {
        modal: false,
    }

    handleClose = () => this.setState({modal: false});
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        this.setState({modal: nextProps.modal})
    }

    render() {
        return (
            <>

                <Modal show={this.state.modal} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body> <AddSubCategory/> </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </>
        );
    }
}
