import React, {Component} from 'react';
import {Form, Button, Row, Col, ListGroup, Alert, Modal, Container, FormControl} from 'react-bootstrap'
import {useState, useEffect, useRef} from 'react'
import Media from "./Media";

function alertClicked() {
    alert('You clicked the third ListGroupItem');
}

class Location extends Component {
    state = {
        modal: false,
        categories: ['AutoMobiles', 'Education', 'Constructions', 'Banks', 'Electrical'],
        locationCategories: [],
        selectedCategory: this.categories[0]
    }

    onCategoryChange = (e) => {
        this.setState({selectedCategory: e.target.value})
    }
    addCategory = () => {
        let locationCategories = [...this.state.locationCategories];
        locationCategories.push(this.state.selectedCategory);
        this.setState({locationCategories});
    }

    render() {
        return (
            <div>

                <Container class>
                    <h2>Add Location</h2>


                    <Form>
                        <Form.Group as={Row}>
                            <Form.Label column sm="2">
                                Name (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location name"/>
                            </Col>
                            <Form.Label column sm="2">
                                Name (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location name (Arabic)"/>
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">
                                Description (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location Description"/>
                            </Col>
                            <Form.Label column sm="2">
                                Description (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location Description (Arabic)"/>
                            </Col>
                        </Form.Group>


                        <Form.Group>
                            <Form.Label>
                                Please choose category
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <FormControl as={'select'} onChange={this.onCategoryChange}>
                                        {this.state.categories.map((category) => {
                                            return (
                                                <option>{category}</option>
                                            )
                                        })}
                                    </FormControl>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addCategory()}>Add<i
                                        className="fa fa-plus"/></Button>
                                </Form.Group>
                            </Form.Row>
                        </Form.Group>

                        <ListGroup>


                            {this.state.locationCategories.length !== 0 ? this.state.locationCategories.map((category) => {
                                return (
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <p className={'col'}>{category}</p>
                                            <Button variant="danger">Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                )
                            }) : <p className={'col'}>No categories yet</p>}


                        </ListGroup>


                        <Media/>

                        <div className={'p-2'}/>

                        <Example showModal={this.state.modal}/>
                        <Button variant="primary" onClick={() => {
                            this.setState({modal: true})
                        }}>Submit</Button>
                    </Form>


                </Container>


            </div>
        );
    }
}

export default Location;

class Example extends Component {
    state = {
        modal: false,
    }

    handleClose = () => this.setState({modal: false});
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        this.setState({modal: nextProps.showModal})
    }

    render() {
        return (
            <>

                <Modal show={this.state.modal} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </>
        );
    }
}

//export default Example;
