import React, { Component } from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

class SocialMedia extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            imageUrls: [],
            videoUrls:[],
            type:'image',
            socialMediaList:this.props.socialMediaList,
            platform:[]
        };
        this._handleImageChange = this._handleImageChange.bind(this);
        this._handleSubmit = this._handleSubmit.bind(this);
    }

    _handleSubmit(e) {
        e.preventDefault();
        // TODO: do something with -> this.state.file
    }

    _handleImageChange(e) {
        e.preventDefault();
        //reset the chosen photos
        //this.setState({files:[],imageUrls:[]})


        let files = Array.from(e.target.files);


        console.log(files)
        files.map((file,index) =>{
            console.log(file,'inside loop')
            let reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onloadend = () => {
                let files = [...this.state.files];
                files.push(file);
                let images = [...this.state.imageUrls];
                images.push(reader.result);
                this.setState({
                    files: files,
                    imageUrls:images
                });

            }
        })

    }

    removeImageFile = (index) =>{
        let files =[...this.state.files];
        let imageUrls =[...this.state.imageUrls];
        files.splice(index,1);
        imageUrls.splice(index,1);
        this.setState({files,imageUrls})
    };
    removeVideo = (index) =>{
        let videoUrls =[...this.state.videoUrls];
        videoUrls.splice(index,1);
        this.setState({videoUrls})
    };
    videoType = (e) =>{
        console.log('video select change', e.target.value);
    }
    addVideo = () =>{
        //check video type and then see
    };

    componentWillReceiveProps(nextProps, nextContext) {
        console.log('will rcv props',nextProps)
        this.setState({socialMediaList:nextProps.socialMediaList})
    }

    render() {


        return (
            <div>
                <div>
                    <h2>Social Media</h2>
                </div>

                <Form.Group>
                    <Form.Label>
                        Add Social Media
                    </Form.Label>
                    <Form.Row>
                        <Form.Group as={Col}>
                            <FormControl as={'select'} onChange={this.videoType}>
                                <option>Upload</option>
                                <option>Link/Youtube</option>
                            </FormControl>
                        </Form.Group>

                        <Form.Group as={Col}>
                            <Form.Control type="text" placeholder="Social media link"/>
                        </Form.Group>

                        <Form.Group as={Col} className={'center'}>
                            <Button className="badge-info col-6" onClick={()=>this.addVideo()}>Add<i className="fa fa-plus"/></Button>
                        </Form.Group>
                    </Form.Row>
                </Form.Group>

                <ListGroup>
                    {this.state.socialMediaList.length !== 0 ? this.state.socialMediaList.map((socialMedia, index) => {
                            let className = 'fa fa-'+socialMedia.platform;
                        return (
                                <ListGroup.Item>
                                    <div className={'row'}>
                                        <i className={className} style={{width: 64}}/>
                                        <FontAwesomeIcon icon={'facebook'} style={{width:64}} />
                                        <p className={'col'}>{socialMedia.link}</p>
                                        <Button variant="danger"
                                                onClick={() => this.removeCategory(index)}>Remove</Button>
                                    </div>
                                </ListGroup.Item>
                            )
                        }) :
                        <ListGroup.Item>
                            <p className={'col'}>No Social media yet</p>
                        </ListGroup.Item>
                    }


                </ListGroup>




            </div>
        )
    }


}

export default SocialMedia;
