import React, { Component } from 'react';
import {Button, Col, Container, Form, FormControl, ListGroup, Row} from "react-bootstrap";
import Media from "./Media";

class AddCategory extends Component {

    render() {
        return (
            <Container class>
                <h2>Add Category</h2>


                <Form>
                    <Form.Group as={Row}>
                        <Form.Label column sm="2">
                            Name (English)
                        </Form.Label>
                        <Col sm="4">
                            <Form.Control type="text" placeholder="Category name"/>
                        </Col>
                        <Form.Label column sm="2">
                            Name (Arabic)
                        </Form.Label>
                        <Col sm="4">
                            <Form.Control type="text" placeholder="Category name (Arabic)"/>
                        </Col>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>
                            Please add subcategory
                        </Form.Label>
                        <Form.Row>
                            <Form.Group as={Col}>
                                <FormControl as={'select'} onChange={this.onCategoryChange}>
                                    {this.state.categories.map((category) => {
                                        return (
                                            <option>{category}</option>
                                        )
                                    })}
                                </FormControl>
                            </Form.Group>

                            <Form.Group as={Col} className={'center'}>
                                <Button className="badge-info col-6" onClick={() => this.addCategory()}><i
                                    className="fa fa-plus"/> Add</Button>
                            </Form.Group>
                        </Form.Row>
                    </Form.Group>

                    <ListGroup>


                        {this.state.locationCategories.length !== 0 ? this.state.locationCategories.map((category, index) => {
                                return (
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <p className={'col'}>{category}</p>
                                            <Button variant="danger"
                                                    onClick={() => this.removeCategory(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                )
                            }) :
                            <ListGroup.Item>
                                <p className={'col'}>No categories yet</p>
                            </ListGroup.Item>
                        }


                    </ListGroup>


                    <Media/>

                    <div className={'p-2'}/>

                    <SocialMedia socialMediaList={this.state.locationSocialMedia} updateSocialMediaList={this.updateSocialMediaList}/>

                    <div className={'p-2'}/>

                    <Example showModal={this.state.modal}/>
                    <Button variant="primary" onClick={() => {
                        this.setState({modal: true})
                    }}>Submit</Button>
                </Form>


            </Container>

        );
    }
}

export default AddCategory;
