import React, {Component} from 'react';
import {Form, Button, Row, Col, ListGroup, Alert, Modal, Container, FormControl} from 'react-bootstrap'
import {useState, useEffect, useRef} from 'react'
import Media from "./Media";
import SocialMedia from "./SocialMedia";
import baseService from "../services/baseService";
import DragableList from "../customComponents/DragableList";
import SubScriptionControl from "./SubScriptionControl";
import MapView from "../customComponents/MapView";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css

class AddPlace extends Component {
    state = {
        modal: false,
        categories: [],
        name: '',
        nameAr: '',
        desc: '',
        descAr: '',
        media: [],
        loading: true,
        title: 'Add',
        edit: false,
        locations: [],
        selectedLocationIndex: 0,
        selectedCategoryIndex: 0,
        selectedSubCategoryIndex: 0,
        phones: [],
        emails: [],
        workingHours: [],
        website: '',
        phone: '',
        email: '',
        workingHourFrom: '',
        workingHourTo: '',
        subscriptionInfo: {},
        place: {},
        socialMedia: [],
        mapLocation: {}


    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({categories, loading: false}));
        baseService.getLocations().then((locations) => this.setState({locations}));
        this.checkProps(this.props);

    }

    updateSubscriptionInfo = (subscriptionInfo) => {
        console.log("iam updating the subscription info", subscriptionInfo)
        this.setState({subscriptionInfo});
    }
    checkProps = (nextProps) => {
        if (nextProps.edit) {
            let place = nextProps.place;
            if (place) {
                console.log("I;m setting the new check Props");
                console.log('locations ',this.state.locations,'\n categories',this.state.categories,'\n gotten place',place)
                //edit category
                this.setState({
                    place: nextProps.place,
                    edit: true,
                    title: 'Edit',
                    name: place.name,
                    nameAr: place.nameAr,
                    desc: place.desc,
                    descAr: place.descAr,
                    media: place.media,
                    phones: place.phone,
                    emails: place.email,
                    workingHours: place.workingHours,
                    website: place.website,
                    subscriptionInfo: place.subscriptionInfo,
                    socialMedia: place.socialMedia,
                    mapLocation: place.mapLocation,
                    selectedLocationIndex: 0,
                    selectedCategoryIndex: 0,
                    selectedSubCategoryIndex: 0,

                })
            }

        }
    }

    alert = (title) => {
        confirmAlert({
            title: '' + title,
            message: '',
            buttons: [{label: 'Dismiss'}]
        });
    };

    componentWillReceiveProps(nextProps, nextContext) {
        this.checkProps(nextProps);

    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    };

    onLocationChange = (e) => {
        this.setState({selectedLocationIndex: e.target.selectedIndex})
    };
    onCategoryChange = (e) => {
        this.setState({selectedCategoryIndex: e.target.selectedIndex})
    };
    onSubCategoryChange = (e) => {
        this.setState({selectedSubCategoryIndex: e.target.selectedIndex})
    };
    addCategory = () => {
        let locationCategories = [...this.state.locationCategories];
        if (locationCategories.includes(this.state.categories[this.state.selectedCategory])) {
            this.alert('AddLocation already has this category');
            return;
        }
        locationCategories.push(this.state.categories[this.state.selectedCategory]);
        this.setState({locationCategories});
    };

    updateSocialMediaList = (socialMediaList) => {
        //keep social media sync
        this.setState({socialMedia: socialMediaList});

    };
    updateMediaFiles = (media) => {
        console.log("updating media to ",media);
        this.setState({media});
    }

    addPhone = () => {
        let phones = [...this.state.phones];
        phones.push(this.state.phone);
        this.setState({phones, phone: ''});
    };
    removePhone = (index) => {
        let phones = [...this.state.phones];
        phones.splice(index, 1)
        this.setState({phones});
    };
    addEmail = () => {
        let emails = [...this.state.emails];
        emails.push(this.state.email);
        this.setState({emails, email: ''});
    };
    removeEmail = (index) => {
        let emails = [...this.state.emails];
        emails.splice(index, 1);
        this.setState({emails});
    };
    timeFormat = (time) => {
        let timeSplit = time.split(':'),
            hours,
            minutes,
            meridian;
        hours = timeSplit[0];
        minutes = timeSplit[1];
        if (hours > 12) {
            meridian = 'PM';
            hours -= 12;
        } else if (hours < 12) {
            meridian = 'AM';
            if (hours === 0) {
                hours = 12;
            }
        } else {
            meridian = 'PM';
        }
        return hours + ':' + minutes + ' ' + meridian;
        //alert(hours + ':' + minutes + ' ' + meridian);
    }
    addWorkingHour = () => {
        let workingFrom = this.state.workingHourFrom;
        let workingTo = this.state.workingHourTo;
        if (!workingFrom || !workingTo) {
            this.alert('Please choose correct time');
            return;
        }

        let workingHours = [...this.state.workingHours];
        let workingHour = this.timeFormat(workingFrom) + " To " + this.timeFormat(workingTo);
        workingHours.push(workingHour);
        this.setState({workingHours, workingHourFrom: '', workingHourTo: ''});
    };
    removeWorkingHour = (index) => {
        let workingHours = [...this.state.workingHours];
        workingHours.splice(index, 1);
        this.setState({workingHours});
    };
    updateMap = (position, address) => {
        let mapLocation = {
            longitude: position.lng,
            latitude: position.lat,
            address: address
        };
        this.setState({mapLocation})
    };
    validation = (place) => {
        console.log('validating ',place);
        let valid = true;
        if (place) {
            if (
                place.name === '' ||
                place.nameAr === '' ||
                place.desc === '' ||
                place.descAr === '' ||
                place.category === '' ||
                place.subCategory === '' ||
                place.phone.length === 0 ||
                place.email.length === 0 ||
                place.media.length === 0 ||
                place.location === '' ||
                place.website === '' ||
                place.mapLocation === {} ||
                place.subscriptionInfo === {}
            )
                valid = false;
        }
        return valid;
    }
    addPlace = () => {
        console.log(this.state.categories)
        let category = this.state.categories[this.state.selectedCategoryIndex]._id;//get categoryId from the selected one

        let subCategory = this.state.categories[this.state.selectedCategoryIndex].subCategories[this.state.selectedSubCategoryIndex]._id;
        let location = this.state.locations[this.state.selectedLocationIndex]._id;
        //do validation
        let place;
        if (this.state.edit) {
            place = this.state.place;


            place.name = this.state.name;
            place.nameAr = this.state.nameAr;
            place.desc = this.state.desc;
            place.descAr = this.state.descAr;
            place.media = this.state.media;
            place.category = category;
            place.subCategory = subCategory;
            place.phone = this.state.phones;
            place.email = this.state.emails;
            place.workingHours = this.state.workingHours;
            place.website = this.state.website;
            place.location = location;
            place.mapLocation = this.state.mapLocation;
            place.socialMedia = this.state.socialMedia;
            place.subscriptionInfo = this.state.subscriptionInfo;
            if (this.validation(place)) {
                baseService.updatePlace(place).catch((err) => console.log(err));
                if (this.props && this.props.updatePlace)
                    this.props.updatePlace();
            } else
                this.alert('Please fill all fields')


        } else {
            place = {
                name: this.state.name,
                nameAr: this.state.nameAr,
                desc: this.state.desc,
                descAr: this.state.descAr,
                media: this.state.media,
                category: category,
                subCategory: subCategory,
                phone: this.state.phones,
                email: this.state.emails,
                workingHours: this.state.workingHours,
                website: this.state.website,
                location: location,
                mapLocation: this.state.mapLocation,
                socialMedia: this.state.socialMedia,
                subscriptionInfo: this.state.subscriptionInfo,


            };
            if (this.validation(place)) {
                baseService.addPlace(place).then((response) => console.log(response)).catch((err) => console.log(err))

            } else
                this.alert('Please fill all fields')
        }
        if (this.props && this.props.handleClose && this.validation(place))
            this.props.handleClose()

    }

    render() {
        if (this.state.categories.length === 0)
            return (<div style={{color: 'red'}}>
                Please Add Location first
                <Button className="badge-info col-6" href={'/Categories'}>
                    <i className="fa fa-plus"/> Add Category

                </Button>
            </div>);

        return (
            <div>
                <Container class>
                    {!this.props.handleClose && <h2> Add Place</h2>}
                    <Form>
                        <Form.Group as={Row}>
                            <Form.Label column sm="2">
                                Name (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Place name"
                                              onChange={(e) => this.onChange('name', e)} value={this.state.name}/>
                            </Col>
                            <Form.Label column sm="2">
                                Name (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Place name (Arabic)"
                                              onChange={(e) => this.onChange('nameAr', e)} value={this.state.nameAr}/>
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">
                                Description (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control as={'textarea'} placeholder="Place Description"
                                              onChange={(e) => this.onChange('desc', e)} value={this.state.desc}/>
                            </Col>
                            <Form.Label column sm="2">
                                Description (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control as={'textarea'} placeholder="Place Description (Arabic)"
                                              onChange={(e) => this.onChange('descAr', e)} value={this.state.descAr}/>
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">
                                Website
                            </Form.Label>
                            <Col>
                                <Form.Control type="text" placeholder="Website"
                                              onChange={(e) => this.onChange('website', e)} value={this.state.website}/>
                            </Col>

                        </Form.Group>


                        <Form>
                            <Form.Group>

                                <Form.Row>
                                    <Form.Group as={Col}>

                                        <Form.Label>
                                            Please choose location
                                        </Form.Label>
                                        <FormControl as={'select'} onChange={this.onLocationChange}>
                                            {this.state.locations.map((location, index) => {
                                                return (
                                                    <option key={index}>{location.name} / {location.nameAr}</option>
                                                )
                                            })}
                                        </FormControl>
                                    </Form.Group>

                                    <Form.Group as={Col}>

                                        <Form.Label>
                                            Please choose Category
                                        </Form.Label>
                                        <FormControl as={'select'} onChange={this.onCategoryChange}>
                                            {this.state.locations[this.state.selectedLocationIndex] &&
                                            this.state.locations[this.state.selectedLocationIndex].categories.map((category, index) => {
                                                return (
                                                    <option key={index}>{category.name} / {category.nameAr}</option>
                                                )
                                            })}
                                        </FormControl>
                                    </Form.Group>

                                    <Form.Group as={Col}>

                                        <Form.Label>
                                            Choose SubCategory
                                        </Form.Label>
                                        <FormControl as={'select'} onChange={this.onSubCategoryChange}>
                                            {this.state.locations[this.state.selectedLocationIndex] &&
                                            this.state.locations[this.state.selectedLocationIndex].categories[this.state.selectedCategoryIndex].subCategories.map((category, index) => {
                                                return (
                                                    <option key={index}>{category.name} / {category.nameAr}</option>
                                                )
                                            })}
                                        </FormControl>
                                    </Form.Group>


                                </Form.Row>
                            </Form.Group>
                        </Form>


                        <Form.Group>
                            <Form.Label>
                                Please Add phone
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Control type="text" placeholder="Phone Number"
                                                  onChange={(e) => this.onChange('phone', e)} value={this.state.phone}/>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addPhone()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                            {this.state.phones.length !== 0 ? this.state.phones.map((phone, index) => {
                                    return (
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <p className={'col'}>{phone}</p>
                                                <Button variant="danger"
                                                        onClick={() => this.removePhone(index)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>)
                                })
                                :
                                <ListGroup.Item>
                                    <p className={'col'}>No Phones added yet</p>
                                </ListGroup.Item>
                            }
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>
                                Please Add Email
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Control type="text" placeholder="Email"
                                                  onChange={(e) => this.onChange('email', e)} value={this.state.email}/>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addEmail()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                            {this.state.emails.length !== 0 ? this.state.emails.map((email, index) => {
                                    return (
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <p className={'col'}>{email}</p>
                                                <Button variant="danger"
                                                        onClick={() => this.removeEmail(index)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>)
                                })
                                :
                                <ListGroup.Item>
                                    <p className={'col'}>No Emails added yet</p>
                                </ListGroup.Item>
                            }
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>
                                Please Working Hours
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Control type="time" placeholder="Working Hours From"
                                                  onChange={(e) => this.onChange('workingHourFrom', e)}
                                                  value={this.state.workingHourFrom}/>

                                </Form.Group>
                                <p>To</p>
                                <Form.Group as={Col}>
                                    <Form.Control type="time" placeholder="Working Hours To"
                                                  onChange={(e) => this.onChange('workingHourTo', e)}
                                                  value={this.state.workingHourTo}/>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addWorkingHour()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                            {this.state.workingHours.length !== 0 ? this.state.workingHours.map((workingHour, index) => {
                                    return (
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <p className={'col'}>{workingHour}</p>
                                                <Button variant="danger"
                                                        onClick={() => this.removeWorkingHour(index)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>)
                                })
                                :
                                <ListGroup.Item>
                                    <p className={'col'}>No Working Hours added yet</p>
                                </ListGroup.Item>
                            }
                        </Form.Group>


                        <Media media={this.state.media} updateMedia={this.updateMediaFiles}/>

                        <div className={'p-2'}/>

                        <SocialMedia socialMediaList={this.state.socialMedia}
                                     updateSocialMediaList={this.updateSocialMediaList}/>


                        <SubScriptionControl update={this.updateSubscriptionInfo}
                                             subscriptionInfo={this.state.subscriptionInfo}/>
                        <div className={'p-2'}/>
                        <MapView update={this.updateMap} location={this.state.mapLocation}/>
                        <div className={'p-2'}/>
                        <Button variant="primary"
                                onClick={() => this.addPlace()}>{this.state.title} Place</Button>


                    </Form>


                </Container>


            </div>
        );
    }
}

export default AddPlace;
