import React, {Component} from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
import FormData from 'form-data';
import DragableList from "../customComponents/DragableList";

class Media extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            imageUrls: [],
            videoUrls: [],
            type: 'image',
            externalLink: '',
        };
        this._handleImageChange = this._handleImageChange.bind(this);
    }

    componentWillReceiveProps(nextProps, nextContext) {

        if (nextProps.media) {
            this.setState({files: nextProps.media})
        }
    }

    componentWillMount() {
        if (this.props.media) {
            this.setState({files: this.props.media})
        }
    }


    _handleImageChange = async (e) => {
        e.preventDefault();
        let filesArray = Array.from(e.target.files);
        const formData = new FormData();
        filesArray.map((singleFile) => {
            formData.append('files[]', singleFile);
        });
        let response = await fetch('http://localhost:1111/api/uploadMulti', {
            method: 'POST',
            body: formData,
            headers: {
                'Authorization': "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0eXBlIjoyLCJyZWdEYXRlIjoiV2VkIE9jdCAwMiAyMDE5IDEzOjQ1OjA3IEdNVCswMzAwIChBcmFiaWFuIFN0YW5kYXJkIFRpbWUpIiwiaXNWYWxpZCI6dHJ1ZSwiX2lkIjoiNWQ5NDgyMDNlYmQ1ZTg0M2I4NTU5NDI5IiwiZW1haWwiOiJhbGFhIiwibmFtZSI6ImFsYWEiLCJfX3YiOjAsImlhdCI6MTU3MDAxMzc2N30.LB-CBu-kOlPQwJjoyVwhuFP9lYxBWhffh-479W1emX8"
            },
        });
        if (response.ok) {

            try {

                let filesFetched = await response.json();
                let files = [...this.state.files];
                filesFetched.map((singleFile) => {
                    files.push(singleFile);
                });

                this.setState({files}, () => this.update());
            } catch (e) {
                console.log(e)
            }
        } else {
            console.log('response not ok ', response)
        }


    }

    //TODO later relate this in each update
    update = () => {
        if (this.props && this.props.updateMedia) {
            this.props.updateMedia(this.state.files);
        }
    }

    removeImageFile = (index) => {
        let files = [...this.state.files];
        files.splice(index, 1);
        this.setState({files}, () => {
            this.update()
        });
    };


    addExternalLink = () => {
        //check video type and send it to the server
        if (this.state.externalLink !== '') {
            //check if it's a valid link
            let link = this.state.externalLink.toString().toLowerCase();
            if (link.includes('.mp4') || link.includes('youtube') || link.match(/\.(jpeg|jpg|gif|png)$/)) {
                let type = link.includes('.mp4') || link.includes('youtube') ? 'video' : 'image'
                let files = [...this.state.files];
                files.push({
                    link: this.state.externalLink,
                    type: type
                });
                //let extention = link.substr(link.lastIndexOf('.')+1,link.length)
                //console.log(extention)

                this.setState({externalLink: '', files}, () => {
                    this.update()
                })
            } else {
                alert('invalid video/image url (Accepting MP4 or Youtube link');
                return;
            }
        } else
            alert('Please fill video link first')
    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    }

    render() {
        return (
            <div>
                <div>
                    <h2>Media</h2>
                </div>

                <Tabs id="controlled-tab-example" activeKey={this.state.type} onSelect={k => this.setState({type: k})}>
                    <Tab eventKey="image" title="Upload">
                        <form>
                            <input type="file" onChange={this._handleImageChange} multiple/>
                        </form>


                    </Tab>
                    <Tab eventKey="video" title="External Link">

                        <Form.Group>
                            <Form.Label>
                                Add External Link
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Control type="text"
                                                  placeholder="Video/Image Link (Youtube or external media link) MP4"
                                                  onChange={(e) => this.onChange('externalLink', e)}
                                                  value={this.state.externalLink}
                                    />
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addExternalLink()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                        </Form.Group>

                        {this.state.videoUrls && this.state.videoUrls.map((url, index) => {
                            return (
                                <div>
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <div className={'col'}>

                                                <img style={{width: 100, height: 100, padding: 5}} src={url}/>
                                            </div>
                                            <Button variant="danger"
                                                    onClick={() => this.removeImageFile(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                </div>)
                        })}
                    </Tab>

                </Tabs>


                <DragableList
                    data={this.state.files}
                    renderItem={(file, index) => {
                        return (
                            <div>
                                <ListGroup.Item>
                                    <div className={'row'}>
                                        <div className={'col'}>
                                            {file.type.includes('video') &&
                                            <video style={{width: 200, height: 200, padding: 5}} controls>
                                                <source
                                                    src={file.link}
                                                    type={'video/' + file.link.substr(file.link.lastIndexOf('.') + 1, file.link.length)}
                                                />
                                                Your browser does not support HTML5 video.
                                            </video>
                                            }
                                            {file.type.includes('image') &&
                                            <img style={{width: 100, height: 100, padding: 5}} src={file.link}/>}

                                        </div>
                                        <Button variant="danger"
                                                onClick={() => this.removeImageFile(index)}>Remove</Button>
                                    </div>
                                </ListGroup.Item>
                            </div>)
                    }}
                    reOrder={(files) => {
                        //prevent issue when removing
                        if (this.state.files.length === files.length) {
                            if (files[0].type === 'image') {
                                this.setState({files}, () => {
                                    this.update()
                                });

                            } else {
                                alert('The first item must be an image')
                            }
                        }

                    }}
                />


            </div>
        )
    }


}

export default Media;
