/**
 * react-drag-sort
 *
 * https://github.com/theuprising/react-drag-sort
 * https://npmjs.com/package/react-drag-sort
 * https://unpkg.com/react-drag-sort/umd/react-drag-sort.min.js
 */

import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'
const ReactDragSort = window.ReactDragSort.default;
const React = window.React;
const { render } = window.ReactDOM;

class SortableList extends React.Component {
    constructor (props) {
        super(props)
        this.state = {
            collection: [
                {
                    link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg",
                    type: "image",
                    _id: "5d98e1abf7d5686fb2dab575"
                }
                , {
                    link: "http://localhost:1111/public/imgs/15702971803291555776041y.png",
                    type: "image",
                    _id: "5d98e1abf7d5686fb2dab575"
                } , {
                    link: "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4",
                    type: "video",
                    _id: "5d98e1abf7d5686fb2dab575"
                }
            ]
        }
    }

    Item ({link,type, index, onRemove, onChange, decorateHandle}) {
        return (
            <div>
                {decorateHandle(<span style={{cursor: 'move'}}>+</span>)}
                <div>
                    <ListGroup.Item>
                        <div>
                            <div >
                                {type.includes('video') &&
                                <video style={{width: 100, height: 100, padding: 5}} controls>
                                    <source
                                        src={link}
                                        type={'video/'+link.substr(link.lastIndexOf('.')+1,link.length)}
                                    />
                                    Your browser does not support HTML5 video.
                                </video>
                                }
                                {type.includes('image') &&
                                <img style={{width: 100, height: 100, padding: 5}} src={link}/>}

                            </div>
                            <Button variant="danger"
                                    onClick={() => this.removeImageFile()}>Remove</Button>
                        </div>
                    </ListGroup.Item>
                </div>
            </div>
        )
    }

    render () {
        return (
            <ReactDragSort
                collection={this.state.collection}
                onChange={collection => {
                    this.setState({collection})
                }}
                Component={this.Item}
            />
        )
    }
}

export default SortableList
