import React from 'react';
import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import Home from './components/Home';
import About from './components/About';
import AddLocation from './components/AddLocation';
import NavBar from "./components/NavBar";
import Categories from "./components/Categories";
import AddCategory from "./components/AddCategory";
import Locations from "./components/Locations";
import Places from "./components/Places";
import AddPlace from "./components/AddPlace";
import MapView from "./customComponents/MapView";
import Login from "./components/Login";
import baseService from "./services/baseService";
import Visitors from "./components/Visitors";

function RouterComponent() {
    return (
        <Router>
            <Switch>
                <Route exact path="/" component={Home}/>
                <PrivateRoute exact path="/admin/" component={Home}/>
                <PrivateRoute exact path="/admin/addLocation" component={AddLocation}/>
                <PrivateRoute exact path="/admin/categories" component={Categories}/>
                <PrivateRoute exact path="/admin/about" component={About}/>
                <PrivateRoute exact path="/admin/addCategory" component={AddCategory}/>
                <PrivateRoute exact path="/admin/Locations" component={Locations}/>
                <PrivateRoute exact path="/admin/Places" component={Places}/>
                <PrivateRoute exact path="/admin/addPlace" component={AddPlace}/>
                <PrivateRoute exact path="/admin/MapView" component={MapView}/>
                <PrivateRoute exact path="/admin/visitors" component={Visitors}/>
                <Route exact path="/Logout" component={Logout}/>
                <Route exact path="/Login" component={Login}/>


            </Switch>
        </Router>
    );
}

const PrivateRoute = ({component: Component}, ...rest) => (
    <Route
        {...rest}
        render={(props) => (
            baseService.checkLogin()
                ? <div><NavBar/> <Component {...props} /></div>
                : <Redirect to={{
                    pathname: '/login',
                    state: {from: props.location}
                }}/>
        )}
    />
);

const Logout = withRouter(({history}) =>{
        baseService.logout();
        history.push("/login");
        return null;
}

);


export default RouterComponent;
