import React, {Component} from 'react';
import {Button, Form, ListGroup} from "react-bootstrap";
import baseService from '../services/baseService'

class Categories extends Component {
    state = {
        categories: [],
        subCategories:[],
        loading: true
    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({
            categories,
            subCategories:categories[0].subCategories,
            loading: false
        })).catch((err) => console.log(err));
    }

    render() {
        return (
            <div>
                <h2>Categories</h2>

                <Form>
                    <Form.Row>
                        <Form.Group>
                            <ListGroup>


                                {this.state.categories.length !== 0 ? this.state.categories.map((category, index) => {
                                        return (
                                            <ListGroup.Item onClick={()=>this.onCategoryClick(index)}>
                                                <div className={'row'}>
                                                    <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                    <Button variant="danger"
                                                            onClick={() => this.removeCategory(index)}>Remove</Button>
                                                </div>
                                            </ListGroup.Item>
                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No categories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>
                            <Form.Group>
                                <ListGroup>


                                    {this.state.subCategories.length !== 0 ? this.state.subCategories.map((category, index) => {
                                            return (
                                                <ListGroup.Item >
                                                    <div className={'row'}>
                                                        <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                        <Button variant="danger"
                                                                onClick={() => this.removeSubCategory(index)}>Remove</Button>
                                                    </div>
                                                </ListGroup.Item>
                                            )
                                        }) :
                                        <ListGroup.Item>
                                            <p className={'col'}>No categories yet</p>
                                        </ListGroup.Item>
                                    }


                                </ListGroup>
                            </Form.Group>
                        </Form.Group>
                    </Form.Row>
                </Form>
            </div>
    );
    }
    }

    export default Categories;
