import React, {Component} from 'react';
import {Form, Button, Row, Col, ListGroup, Alert, Modal, Container, FormControl} from 'react-bootstrap'
import {useState, useEffect, useRef} from 'react'
import Media from "./Media";
import SocialMedia from "./SocialMedia";
import baseService from "../services/baseService";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css

class AddLocation extends Component {
    state = {
        modal: false,
        categories: [],
        name: '',
        nameAr: '',
        desc: '',
        descAr: '',
        locationCategories: [],
        media: [],
        locationSocialMedia: [],
        selectedCategory: 0,
        loading: true,
        title: 'Add',
        edit: false,
        location: {},


    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({categories, loading: false}))
        this.checkProps(this.props);

    }

    checkProps = (nextProps) => {
        if (nextProps.edit) {
            let location = nextProps.location;
            if (location) {
                //edit category
                this.setState({
                    location: nextProps.location,
                    edit: true,
                    title: 'Edit',
                    name: location.name,
                    nameAr: location.nameAr,
                    desc: location.desc,
                    descAr: location.descAr,
                    locationCategories: location.categories,
                    media: location.media,
                    locationSocialMedia: location.socialMedia,

                })
            }

        }
    }

    alert = (title) => {
        confirmAlert({
            title: '' + title,
            message: '',
            buttons: [{label: 'Dismiss'}]
        });
    };

    componentWillReceiveProps(nextProps, nextContext) {
        this.checkProps(nextProps);

    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    };
    onCategoryChange = (e) => {
        this.setState({selectedCategory: e.target.selectedIndex})
    };
    addCategory = () => {
        let locationCategories = [...this.state.locationCategories];
        if (locationCategories.includes(this.state.categories[this.state.selectedCategory])) {
            this.alert('AddLocation already has this category');
            return;
        }
        locationCategories.push(this.state.categories[this.state.selectedCategory]);
        this.setState({locationCategories});
    };

    removeCategory = (index) => {

        let locationCategories = [...this.state.locationCategories];
        locationCategories.splice(index, 1);
        this.setState({locationCategories});
    };
    updateSocialMediaList = (socialMediaList) => {
        //keep social media sync
        this.setState({locationSocialMedia: socialMediaList});

    };
    updateMediaFiles = (media) => {
        this.setState({media});
    }
    addLocation = () => {
        let categories = [];
        this.state.locationCategories.map((category) => categories.push(category._id));
        const {name, nameAr, desc, descAr, media, locationSocialMedia} = this.state;

        if (categories.length === 0) {
            this.alert('Please add categories to the location');
            return;
        } else if (media.length === 0 || name === '' || nameAr === '' || desc === '' || descAr === '') {
            this.alert('Please fill all the fields');
            return;
        }

        if (this.state.edit) {
            let location = this.state.location;

            location.name = this.state.name;
            location.nameAr = this.state.nameAr;
            location.desc = this.state.desc;
            location.descAr = this.state.descAr;
            location.categories = categories;
            location.media = this.state.media;
            location.socialMedia = this.state.locationSocialMedia;

            baseService.updateLocation(location).then((response) =>{
                if (this.props && this.props.updateLocation)
                    this.props.updateLocation();
            }).catch((err) => console.log(err));


        } else {
            let location = {
                name: this.state.name,
                nameAr: this.state.nameAr,
                desc: this.state.desc,
                descAr: this.state.descAr,
                categories: categories,
                media: this.state.media,
                socialMedia: this.state.locationSocialMedia,
            };
            //just add it to the server
            baseService.addLocation(location).catch((err) => console.log(err));
            if(this.props &&this.props.handleClose){
                //close the modal
                this.props.handleClose()
            }
        }


    };

    render() {
        if (this.state.categories.length === 0)
            return (<div style={{color: 'red'}}>
                Please Add Category first
                <Button className="badge-info col-6" href={'/Categories'}>
                    <i className="fa fa-plus"/> Add Category

                </Button>
            </div>);
        return (
            <div >

                <Container class>


                    <Form>
                        <Form.Group as={Row}>
                            <Form.Label column sm="2">
                                Name (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location name"
                                              onChange={(e) => this.onChange('name', e)} value={this.state.name}/>
                            </Col>
                            <Form.Label column sm="2">
                                Name (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control type="text" placeholder="Location name (Arabic)"
                                              onChange={(e) => this.onChange('nameAr', e)} value={this.state.nameAr}/>
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formPlaintextPassword">
                            <Form.Label column sm="2">
                                Description (English)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control as="textarea" placeholder="Location Description"
                                              onChange={(e) => this.onChange('desc', e)} value={this.state.desc}/>
                            </Col>
                            <Form.Label column sm="2">
                                Description (Arabic)
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control as="textarea" placeholder="Location Description (Arabic)"
                                              onChange={(e) => this.onChange('descAr', e)} value={this.state.descAr}/>
                            </Col>
                        </Form.Group>


                        <Form.Group>
                            <Form.Label>
                                Please choose category
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <FormControl as={'select'} onChange={this.onCategoryChange}>
                                        {this.state.categories.map((category, index) => {
                                            return (
                                                <option key={index}>{category.name} / {category.nameAr}</option>
                                            )
                                        })}
                                    </FormControl>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6" onClick={() => this.addCategory()}><i
                                        className="fa fa-plus"/> Add</Button>
                                </Form.Group>
                            </Form.Row>
                        </Form.Group>

                        <ListGroup>


                            {this.state.locationCategories.length !== 0 ? this.state.locationCategories.map((category, index) => {
                                    return (
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                <Button variant="danger"
                                                        onClick={() => this.removeCategory(index)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>
                                    )
                                }) :
                                <ListGroup.Item>
                                    <p className={'col'}>No categories yet</p>
                                </ListGroup.Item>
                            }


                        </ListGroup>


                        <Media media={this.state.media} updateMedia={this.updateMediaFiles}/>

                        <div className={'p-2'}/>

                        <SocialMedia socialMediaList={this.state.locationSocialMedia}
                                     updateSocialMediaList={this.updateSocialMediaList}/>

                        <div className={'p-2'}/>

                        <Example showModal={this.state.modal}/>
                        <Button variant="primary"
                                onClick={() => this.addLocation()}>{this.state.title} Location</Button>
                    </Form>


                </Container>


            </div>
        );
    }
}

export default AddLocation;

class Example extends Component {
    state = {
        modal: false,
    }

    handleClose = () => this.setState({modal: false});
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        this.setState({modal: nextProps.showModal})
    }

    render() {
        return (
            <div className={"modal-dialog modal-lg"}>

                <Modal show={this.state.modal} onHide={this.handleClose} >
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}

//export default Example;
