import React, {Component} from 'react';
import {Form, Button} from "react-bootstrap";

class Login extends Component {
    state = {
        email: '',
        password: '',
        error: '',
    };


    dismissError = () => {
        this.setState({error: ''});
    }

    handleSubmit = (evt) => {
        evt.preventDefault();

        if (!this.state.email || this.state.email ==='') {
            return alert('Email is required')
        }

        if (!this.state.password || this.state.password ==='') {
            return alert('Password is required');
        }


    }

    onChange = (key,e) => {
        this.setState({[key]:e.target.value});
    };


    render() {

        return (
            <div className="Login">
                <Form>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" value={this.state.email} onChange={(e)=>this.onChange('email',e)}/>
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={(e)=>this.onChange('password',e)}/>
                    </Form.Group>

                    <Button variant="primary" type="submit" onClick={(e)=>this.handleSubmit(e)}>
                        Login
                    </Button>
                </Form>
            </div>
        );
    }
}

export default Login;
