import React, {Component} from 'react';
import Geosuggest from 'react-geosuggest';
import Autocomplete from 'react-google-autocomplete';
import LocationPicker from 'react-location-picker';
import Map from "./Map";
import {GoogleMap} from "react-google-maps";

/* Default position */


class MapView extends Component {
    constructor(props) {
        super(props);

        this.state = {
            address: "New Cairo",
            position: {
                lat: 30.017792,
                lng: 31.3473752,
            },

        };

        // Bind
        this.handleLocationChange = this.handleLocationChange.bind(this);
    }
    componentWillReceiveProps(nextProps, nextContext) {
        this.checkProps(nextProps);
    }
    componentWillMount() {
        this.checkProps(this.props);
    }
    checkProps = (props) =>{
        if(props && props.location){
            let location = props.location;
            this.setState({
                address:location.address,
                lat:location.latitude,
                lng:location.longitude
            })
        }
    }

    update = () =>{
        if(this.props && this.props.update)
            this.update(this.state.position,this.state.address)
    }
    handleLocationChange({position, address, places}) {

        // Set new location
        this.setState({position},()=>this.update());
    }

    onSuggestSelect = (suggest) => {
        if (suggest && suggest.label)
            this.setState({position: suggest.location, address: suggest.label},()=>this.update());
        console.log(suggest);

            //this._geoSuggest.blur();

    }

    render() {
        return (
            <div>
                <h4>{this.state.address}</h4>
                <div>
                    <Geosuggest
                        style={{
                            width: '100%',
                            height: '40px',
                            paddingLeft: '16px',
                            marginTop: '2px',
                            marginBottom: '100px'
                        }}
                        ref={el => this._geoSuggest = el}
                        country={'EG'}
                        onSuggestSelect={this.onSuggestSelect}
                    />
                    <LocationPicker
                        containerElement={<div style={{height: '100%'}}/>}
                        mapElement={<div style={{height: '400px'}}/>}
                        defaultPosition={{
                            lat: this.state.position.lat,
                            lng: this.state.position.lng
                        }}
                        onChange={this.handleLocationChange}
                        radius={1}
                        zoom={15}
                    />


                </div>
            </div>
        )
    }
}

export default MapView
