import React, { Component } from 'react';
import baseService from '../services/baseService'
import {Table} from "react-bootstrap";

class Locations extends Component {
    state = {
        locations:[],
        loading:true
    }
    componentWillMount() {
        baseService.getLocations().then((locations) =>{
            this.setState({locations,loading:false})
        }).catch((err)=>console.log(err))
    }

    render() {
        return (
            <div>
                <h2>Locations</h2>
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Name Ar</th>
                        <th>Desc</th>
                        <th>Desc Ar</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.locations.map((location,index)=>{
                        return(
                            <tr>
                                <td>{index}</td>
                                <td>{location.name}</td>
                                <td>{location.nameAr}</td>
                                <td>{location.desc}</td>
                                <td>{location.descAr}</td>
                                <td> <i className="fa fa-trash fa-2x" style={{color: 'red'}}/><i className="fa fa-edit"/></td>

                            </tr>
                            )

                    })}

                    </tbody>
                </Table>
            </div>
        );
    }
}

export default Locations;
