import React, {Component} from 'react';
import baseService from '../services/baseService'
import {Button, Form, Modal, Table} from "react-bootstrap";
import AddPlace from "./AddPlace";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css

class Places extends Component {
    state = {
        places: [],
        loading: true,
        modal: false,
        index: -1,
        edit: false,

    }

    componentWillMount() {
        this.fetchPlaces();
    }

    fetchPlaces = () => {
        baseService.getPlaces().then((places) => {
            this.setState({places, loading: false})
        }).catch((err) => console.log(err))
    };

    addLocation = () => {

    };

    alert = (title) => {
        confirmAlert({
            title: '' + title,
            message: '',
            buttons: [{label: 'Dismiss'}]
        });
    };

    removePlace = async (place) => {
        if (window.confirm("Are you sure you want to delete this place?")){
            let response = await baseService.deletePlace(place);
            if (response) {
                this.fetchPlaces();//reload
            } else {
                this.alert('failed to delete the place')
            }
        }


    };

    render() {

        return (
            <div>
                <h2>Places/Companies</h2>
                <Button variant="success" onClick={() => this.setState({modal: true})}>
                    <i className="fa fa-plus"/> Add Place
                </Button>

                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Name Ar</th>
                        <th>Desc</th>
                        <th>Desc Ar</th>
                        <th>Social Media</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.places.map((place, index) => {
                        return (
                            <tr>
                                <td>{index}</td>
                                <td>{place.name}</td>
                                <td>{place.nameAr}</td>
                                <td>{place.desc}</td>
                                <td>{place.descAr}</td>
                                <td>{place.socialMedia && place.socialMedia.map((singleSocialMedia) => {
                                    return (
                                        <div>{singleSocialMedia.link}</div>
                                    )
                                })}</td>
                                <td>
                                    <i className="fa fa-trash fa-2x" style={{color: 'red'}}
                                       onClick={() => this.removePlace(place)}/>
                                    <i className="fa fa-edit fa-2x" style={{color: 'green'}}
                                       onClick={() => this.setState({index, edit: true, modal: true})}/>
                                </td>

                            </tr>
                        )

                    })}

                    </tbody>
                </Table>
                <AddPlaceModal modal={this.state.modal}
                               place={this.state.places[this.state.index]}
                               edit={this.state.edit}
                               updatePlace={() => {
                                   this.fetchPlaces();
                                   this.setState({modal: false, edit: false, index: -1});
                               }}
                               handleClose={() => {
                                   this.fetchPlaces();
                                   this.setState({modal: false});
                               }}
                />
            </div>
        );
    }
}

export default Places;

