    handleClose = () => {
        this.setState({modal: false});
        if (this.props && this.props.updatePlace)
            this.props.updatePlace();
        else {
            this.props.handleClose()
        }
    }
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        this.setState({modal: nextProps.modal, edit: nextProps.edit, title: 'Add'});
