    render() {
        return (
            <>

                <Modal show={this.state.modal} onHide={this.handleClose} size="lg">
                    <Modal.Header closeButton>
                        <Modal.Title>{this.state.title} Place</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <AddPlace edit={this.state.edit}
                                  place={this.state.place}
                                  updatePlace={this.props.updatePlace}
                                  handleClose={this.handleClose}
                        />
                    </Modal.Body>

                </Modal>
            </>
        );
    }
}
