import React, {Component} from 'react';
import {Col, Form} from 'react-bootstrap'

class SubScriptionControl extends Component {

    state={
        subscribeChecker:true,
        socialMediaChecker:false,
        videoChecker:false,
        offerChecker:false,
        imageNum:0,
        videoNum:0,
        customPanoramaChecker:false,
        priorityABChecker:false,


    };
    onCheckerChange = (key,e) =>{
        this.setState({[key]:e.target.checked})
}
    render() {


        return (
            <div>
                <div>
                    <h2>Subscription Control</h2>
                </div>

                <Form>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.priorityABChecker}

                                custom
                                type={'checkbox'}
                                label={`Priority (Top Alpha.bet.)`}
                                id={'priority'}
                            />
                        </Form.Group>
                    </Form.Row>

                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.subscribeChecker}

                                custom
                                type={'checkbox'}
                                label={`Subscribed`}
                                id={'subscription'}
                            />
                        </Form.Group>
                    </Form.Row>

                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                custom
                                type={'checkbox'}
                                label={`Social Media`}
                                id={'socialMedia'}
                            />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                custom
                                id={'video'}
                                type={'checkbox'}
                                label={`Video`}
                            />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                custom
                                id={'360'}
                                type={'checkbox'}
                                label={`Custom 360`}
                            />
                        </Form.Group>
                    </Form.Row>

                </Form>


            </div>
        )
    }


}

export default SubScriptionControl;
