import React, {Component} from 'react';
import {Button, Col, Form, ListGroup,Row} from "react-bootstrap";
import baseService from '../services/baseService'
import Table from "react-bootstrap/Table";

class Categories extends Component {
    state = {
        categories: [],
        loading: true,
        selectedIndex:0
    };

    componentWillMount() {
        baseService.getCategories().then((categories) => this.setState({
            categories,
            loading: false
        })).catch((err) => console.log(err));
    }

    onCategoryClick = (index) =>{
      this.setState({selectedIndex:index});
    };
    removeCategory = (index) =>{
        let categories = [...this.state.categories];
        categories.splice(index,1);
        this.setState({categories});
    };
    removeSubCategory = (index) =>{
        let categories = [...this.state.categories];
        let category = categories[this.state.selectedIndex];
        category.subCategories.splice(index,1);
        categories[this.state.selectedIndex] = category;
        this.setState({categories})
    };
    addCategory = () =>{

    };
    addSubCategory = () =>{

    };

    render() {
        return (
            <div>
                <h2>Categories</h2>

                <Form>

                    <Form.Row>
                        <Form.Group>
                            <ListGroup>

                                <ListGroup.Item>
                                    <Button className="badge-info col" onClick={() => this.addCategory()}><i
                                        className="fa fa-plus"/> Add Category</Button>
                                </ListGroup.Item>
                                {this.state.categories.length !== 0 ? this.state.categories.map((category, index) => {
                                    let active = this.state.selectedIndex === index;
                                        return (
                                            this.state.selectedIndex===index ?
                                                <ListGroup.Item onClick={() => this.onCategoryClick(index)} active>
                                                    <div className={'row'}>
                                                        <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                        <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={()=>this.removeCategory(index)}/>
                                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>

                                                    </div>
                                                </ListGroup.Item>
                                            :   <ListGroup.Item onClick={() => this.onCategoryClick(index)} >
                                                    <div className={'row'}>
                                                        <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                        <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={()=>this.removeCategory(index)}/>
                                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>

                                                    </div>
                                                </ListGroup.Item>

                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No categories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>

                        </Form.Group>

                        <Form.Group>
                            <ListGroup>
                                <ListGroup.Item>
                                    <Button className="badge-info col" onClick={() => this.addSubCategory()}><i
                                        className="fa fa-plus"/> Add SubCategory</Button>
                                </ListGroup.Item>
                                {this.state.categories.length > 0 && this.state.categories[this.state.selectedIndex].subCategories.length > 0 ? this.state.categories[this.state.selectedIndex].subCategories.map((category, index) => {
                                        return (
                                            <ListGroup.Item>
                                                <div className={'row'}>
                                                    <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                                    <p className={'col'}>{category.name} / {category.nameAr}</p>
                                                    <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={()=>this.removeSubCategory(index)}/>
                                                    <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                                </div>
                                            </ListGroup.Item>
                                        )
                                    }) :
                                    <ListGroup.Item>
                                        <p className={'col'}>No Sub Categories yet</p>
                                    </ListGroup.Item>
                                }


                            </ListGroup>
                        </Form.Group>
                    </Form.Row>
                </Form>



                <Button variant="secondary"
                        onClick={() => this.removeCategory(index)}>Add</Button>
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Icon</th>
                        <th>Category Name</th>
                        <th>Category Name (Ar)</th>
                        <th>SubCategories</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>

                        {this.state.categories.map((category,index) =>{
                            return(
                                <tr>
                                    <td>{index}</td>
                                    <td>
                                        <center>
                                        <img style={{width: 64, height: 64, padding: 5}} src={category.icon}/>
                                        </center>
                                    </td>
                                    <td>{category.name}</td>
                                    <td>{category.nameAr}</td>
                                    <td>
                                        <Table striped bordered hover>
                                            <thead>
                                            <tr>
                                                <th>#</th>
                                                <th>Icon</th>
                                                <th>Category Name</th>
                                                <th>Category Name (Ar)</th>
                                                <th>Actions</th>
                                            </tr>
                                            </thead>
                                            <tbody>
                                        {category.subCategories.map((subCategory,subCategoryIndex)=>{
                                            //do whatever
                                            return (
                                                <tr>
                                                    <td>{subCategoryIndex}</td>
                                                    <td>
                                                        <center>
                                                            <img style={{width: 64, height: 64, padding: 5}} src={subCategory.icon}/>
                                                        </center>
                                                    </td>
                                                    <td>{subCategory.name}</td>
                                                    <td>{subCategory.nameAr}</td>
                                                    <td>
                                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}}/>
                                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                                    </td>
                                                </tr>
                                            )
                                        })}

                                            </tbody>
                                        </Table>
                                    </td>
                                    <td>
                                        <i className="fa fa-trash fa-2x" style={{color: 'red'}}/>
                                        <i className="fa fa-edit fa-2x" style={{color: 'green'}}/>
                                    </td>
                                </tr>
                                )
                        })}

                    </tbody>
                </Table>
            </div>
        );
    }
}

export default Categories;
