    static async getPlacesByCategory(category) {
        let places = await this.getPlaces();
        let filtered = places.filter((item) => item.category === category);
        return filtered;

    }

    static async getPlacesBySubCategory(category, subCategory) {
        let places = await this.getPlaces();
        let filtered = places.filter((item) => item.category === category && item.subCategory === subCategory);
        return filtered;

    }
