import React, {Component} from 'react';
import baseService from '../services/baseService'
import {Button, Form, Modal, Table} from "react-bootstrap";
import AddLocation from "./AddLocation";

class Locations extends Component {
    state = {
        locations: [],
        loading: true,
        modal:false,
        index:-1,
        edit:false,

    }

    componentWillMount() {
        this.fetchLocations();
    }
    fetchLocations =  () =>{
        baseService.getLocations().then((locations) => {
            this.setState({locations, loading: false})
        }).catch((err) => console.log(err))
    }

    addLocation = () =>{

    };
    removeLocation = async (location) =>{
      let response = await baseService.removeLocation(location);
      if(response){
          this.fetchLocations();//reload
      }
      else
      {
          alert('failed to delete the location')
      }

    };
    render() {

        return (
            <div>
                <h2>Locations</h2>
                <Button variant="success" onClick={() => this.setState({modal: true})}>
                    <i className="fa fa-plus"/> Add Location
                </Button>

                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Name Ar</th>
                        <th>Desc</th>
                        <th>Desc Ar</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.locations.map((location, index) => {
                        return (
                            <tr>
                                <td>{index}</td>
                                <td>{location.name}</td>
                                <td>{location.nameAr}</td>
                                <td>{location.desc}</td>
                                <td>{location.descAr}</td>
                                <td>
                                    <i className="fa fa-trash fa-2x" style={{color: 'red'}} onClick={()=>this.removeLocation(location)}/>
                                    <i className="fa fa-edit fa-2x" style={{color: 'green'}} onClick={()=>this.setState({index,edit:true,modal:true})}/>
                                </td>

                            </tr>
                        )

                    })}

                    </tbody>
                </Table>
                <AddLocationModal modal={this.state.modal} location={this.state.locations[this.state.index]} edit={this.state.edit} updateLocation={()=>this.fetchLocations()}/>
            </div>
        );
    }
}

export default Locations;

class AddLocationModal extends Component {
    state = {
        modal: false,
        edit:false,
        location:{},
        title:'Add'

    }

    handleClose = () => this.setState({modal: false});
    handleShow = () => this.setState({modal: true});

    componentWillReceiveProps(nextProps, nextContext) {
        //console.log("i;m recving ",nextProps)
        this.setState({modal: nextProps.modal})
        if(nextProps.edit){
            if(nextProps.location){
                this.setState({title:'Edit',edit:true,location:nextProps.location});
            }
        }
    }

    render() {
        return (
            <>

                <Modal show={this.state.modal} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>{this.state.title} Location</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <AddLocation edit={this.state.edit} location={this.state.location} updateLocation={this.props.updateLocation} />
                    </Modal.Body>

                </Modal>
            </>
        );
    }
}
