import React, {Component} from 'react';

import baseService from "../services/baseService";
import {Form} from "react-bootstrap";

class AddTimeSlot extends Component {
    state = {
        timeSlots: [],
        day: 'Sat',
        from: '',
        to: ''
    };

    componentWillMount() {
        baseService.getTimeSlots().then((response) => console.log(response))
    }

    onChange = (key, e) => {
        this.setState({[key]: e.target.value})
    };

    render() {
        return (
            <div>
                <h2>Add time slot</h2>
                <h5> the current day is {this.state.day} , startinng  from {this.state.from} : {this.state.to}</h5>
                <Form>
                    <Form.Row>
                        <Form.Group controlId="formGroupEmail">
                            <Form.Label>From</Form.Label>
                            <Form.Control type="from" placeholder="enter start time" onChange={(e)=>this.onChange('from',e)}/>
                        </Form.Group>
                        <Form.Group controlId="formGroupEmail">
                            <Form.Label>To</Form.Label>
                            <Form.Control type="to" placeholder="enter ending time" onChange={(e)=>this.onChange('to',e)}/>
                        </Form.Group>


                        <Form.Group controlId="exampleForm.ControlSelect1">
                            <Form.Label>Select day</Form.Label>
                            <Form.Control as="select" onChange={(e) => this.onChange('day', e)}>
                                <option>Sat</option>
                                <option>Sun</option>
                                <option>Mon</option>
                                <option>TUE</option>
                                <option>WED</option>
                                <option>THUR</option>
                                <option>Friday</option>
                            </Form.Control>
                        </Form.Group>
                    </Form.Row>
                </Form>
            </div>
        );
    }
}

export default AddTimeSlot;
