import React, {Component} from 'react';
import {Col, Form, FormControl} from 'react-bootstrap'
import MediaSingle from "./MediaSingle";

class SubScriptionControl extends Component {

    state = {
        subscribeChecker: false,
        socialMediaChecker: false,
        videoChecker: false,
        offerChecker: false,
        imageNum: 0,
        videoNum: 0,
        customPanoramaChecker: false,
        priorityABChecker: false,
        selectedPeriodIndex:0,//0-Year 1-Month
        subscriptionDate:new Date().toISOString().substr(0,10),
        custom360Link:'',
        offerLink:'',



    };

    componentWillMount() {
        this.checkProps(this.props);
    }
    componentWillReceiveProps(nextProps, nextContext) {
        this.checkProps(nextProps)
    }

    checkProps = (props) =>{
        if(props && props.subscriptionInfo){
            this.state = props.subscriptionInfo;
        }
    }

    componentDidMount() {
        this.update();
    }

    update = () =>{
        if(this.props && this.props.update)
            this.props.update(this.state);
    };
    onCheckerChange = (key, e) => {
        this.setState({[key]: e.target.checked}, ()=>this.update())
    }
    onPeriodChange = (e) => {
        this.setState({selectedPeriodIndex: e.target.selectedIndex},()=>this.update())
    };

    updatePanorama = (link)=>{
        this.setState({custom360Link:link},()=>this.update());
    };
    updateOfferLink = (link) =>{
        this.setState({offerLink:link},()=>this.update());
    };
    render() {


        return (
            <div>
                <div>
                    <h2>Subscription Control</h2>
                </div>

                <Form>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.priorityABChecker}
                                onClick={(e) => this.onCheckerChange('priorityABChecker', e)}
                                custom
                                type={'checkbox'}
                                label={`Priority (Top Alpha.bet.)`}
                                id={'priority'}
                            />
                        </Form.Group>
                    </Form.Row>

                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.subscribeChecker}
                                onClick={(e) => this.onCheckerChange('subscribeChecker', e)}
                                custom
                                type={'checkbox'}
                                label={`Subscribed`}
                                id={'subscription'}
                            />
                        </Form.Group>

                    </Form.Row>
                    {this.state.subscribeChecker &&
                    <Form>
                        <Form.Group as={Col}>

                            <Form.Label>
                                Validity
                            </Form.Label>

                            <Form.Control type="date" value={this.state.subscriptionDate} onChange={(e)=>
                                this.setState({subscriptionDate:e.target.value},()=>this.update())} placeholder="Subscriptoin date"/>
                        </Form.Group>

                        <Form.Group as={Col}>

                            <Form.Label>
                                Validity
                            </Form.Label>
                            <FormControl as={'select'} onChange={this.onPeriodChange}>
                                <option key={0}>Year</option>
                                <option key={1}>Month</option>
                            </FormControl>
                        </Form.Group>
                    </Form>
                    }



                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.socialMediaChecker}
                                onClick={(e) => this.onCheckerChange('socialMediaChecker', e)}
                                custom
                                type={'checkbox'}
                                label={`Social Media`}
                                id={'socialMedia'}
                            />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.videoChecker}
                                onClick={(e) => this.onCheckerChange('videoChecker', e)}
                                custom
                                id={'video'}
                                type={'checkbox'}
                                label={`Video`}
                            />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.customPanoramaChecker}
                                onClick={(e) => this.onCheckerChange('customPanoramaChecker', e)}
                                custom
                                id={'360'}
                                type={'checkbox'}
                                label={`Custom 360`}
                            />
                        </Form.Group>
                    </Form.Row>
                    {this.state.customPanoramaChecker && <MediaSingle updateMedia={this.updatePanorama} icon={this.state.custom360Link}/>}
                    <Form.Row>
                        <Form.Group>
                            <Form.Check
                                checked={this.state.offerChecker}
                                onClick={(e) => this.onCheckerChange('offerChecker', e)}
                                custom
                                id={'offer'}
                                type={'checkbox'}
                                label={`has offer`}
                            />
                        </Form.Group>
                    </Form.Row>
                    {this.state.offerChecker && <MediaSingle updateMedia={this.updateOfferLink} icon={this.state.offerLink}/>}
                </Form>


            </div>
        )
    }


}

export default SubScriptionControl;
