import React, { Component } from 'react';
import {Tabs, Tab, ListGroup, Button, Form, Col, FormControl} from 'react-bootstrap'

class Media extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            imageUrls: [],
            videoUrls:[],
            type:'image'
        };
        this._handleImageChange = this._handleImageChange.bind(this);
        this._handleSubmit = this._handleSubmit.bind(this);
    }

    _handleSubmit(e) {
        e.preventDefault();
        // TODO: do something with -> this.state.file
    }

    _handleImageChange(e) {
        e.preventDefault();
        //reset the chosen photos
        //this.setState({files:[],imageUrls:[]})


        let files = Array.from(e.target.files);


        console.log(files)
        files.map((file,index) =>{
            console.log(file,'inside loop')
            let reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onloadend = () => {
                let files = [...this.state.files];
                    files.push(file);
                let images = [...this.state.imageUrls];
                    images.push(reader.result);
                this.setState({
                    files: files,
                    imageUrls:images
                });

            }
        })

    }

    removeImageFile = (index) =>{
        let files =[...this.state.files];
        let imageUrls =[...this.state.imageUrls];
        files.splice(index,1);
        imageUrls.splice(index,1);
        this.setState({files,imageUrls})
    };
    removeVideo = (index) =>{
        let videoUrls =[...this.state.videoUrls];
        videoUrls.splice(index,1);
        this.setState({videoUrls})
    };
    render() {


        return (
            <div>
                <div>
                    <h2>Media</h2>
                </div>

                <Tabs id="controlled-tab-example" activeKey={this.state.type} onSelect={k => this.setState({type:k})}>
                    <Tab eventKey="image" title="Images">
                        <form onSubmit={this._handleSubmit}>
                            <input type="file" onChange={this._handleImageChange} multiple />
                            <button type="submit" onClick={this._handleSubmit}>Upload Image</button>
                        </form>
                        {this.state.imageUrls && this.state.imageUrls.map((url,index) =>{
                            return(
                                <div>
                                    <ListGroup.Item>
                                        <div className={'row'}>
                                            <div className={'col'}>

                                                <img style={{width:100,height:100,padding:5}} src={url}  />
                                            </div>
                                            <Button variant="danger" onClick={()=>this.removeImageFile(index)}>Remove</Button>
                                        </div>
                                    </ListGroup.Item>
                                </div>)

                        })}
                    </Tab>
                    <Tab eventKey="video" title="Videos">

                        <Form.Group>
                            <Form.Label>
                                Add video
                            </Form.Label>
                            <Form.Row>
                                <Form.Group as={Col}>
                                        <Form.Control type="text" placeholder="Video Link (Youtube)"/>
                                </Form.Group>

                                <Form.Group as={Col} className={'center'}>
                                    <Button className="badge-info col-6">Add<i className="fa fa-plus"/></Button>
                                </Form.Group>
                            </Form.Row>
                        </Form.Group>

                        {this.state.videoUrls && this.state.videoUrls.map((url,index) =>{
                            return(
                                    <div>
                                        <ListGroup.Item>
                                            <div className={'row'}>
                                                <div className={'col'}>

                                                    <img style={{width:100,height:100,padding:5}} src={url}  />
                                                </div>
                                                <Button variant="danger" onClick={()=>this.removeVideo(index)}>Remove</Button>
                                            </div>
                                        </ListGroup.Item>
                                    </div>)
                        })}
                    </Tab>

                </Tabs>


            </div>
        )
    }


}

export default Media;
